import React from 'react';
import { AppBar, Toolbar, Container, Typography, Button, Stack, Drawer, List, ListItem, ListItemText, Box } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../actions';

const Navbar = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const user = useSelector((state) => state.user);

  const handleLogout = () => {
    dispatch(logout());
    window.location.href = '/';
  };

  return (
    <Box sx={{ display: 'flex' }}>
      <Sidebar user={user} />
      <AppBar position="fixed" sx={{ backgroundColor: '#232323', color: '#fff', py: 1, boxShadow: 2 }}>
        <Container maxWidth="lg">
          <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
            <Typography variant="h5" component="h2" fontFamily="'Roboto', sans-serif" fontWeight="500">
              Tribunal de Commerce à Casablanca
            </Typography>

            <Stack direction="row" spacing={2} alignItems="center">
              {!user ? (
                <>
                  <Button component={Link} to="/" sx={linkStyle}>Accueil</Button>
                  <Button component={Link} to="/login" sx={linkStyle}>Connexion</Button>
                  <Button component={Link} to="/register" sx={linkStyle}>Inscription</Button>
                </>
              ) : (
                <Button variant="outlined" sx={logoutButtonStyle} onClick={handleLogout}>Déconnexion</Button>
              )}
            </Stack>
          </Toolbar>
        </Container>
      </AppBar>
    </Box>
  );
};

const Sidebar = ({ user }) => {
  return (
    <Drawer
      sx={{
        width: 240,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: 240,
          boxSizing: 'border-box',
          backgroundColor: '#333',
          color: '#fff',
        },
      }}
      variant="permanent"
      anchor="left"
    >
      <List>
        <ListItem button component={Link} to="/">
          <ListItemText primary="Accueil" />
        </ListItem>

        {user ? (
          <>
            {user.role === 'admin' && (
              <ListItem button component={Link} to="/admin">
                <ListItemText primary="Dashboard Admin" />
              </ListItem>
            )}
            {user.role === 'chef' && (
              <ListItem button component={Link} to="/chef">
                <ListItemText primary="Dashboard Chef" />
              </ListItem>
            )}
            {user.role === 'user' && (
              <ListItem button component={Link} to="/dashboard">
                <ListItemText primary="Dashboard User" />
              </ListItem>
            )}
          </>
        ) : null}
      </List>
    </Drawer>
  );
};

const linkStyle = {
  textDecoration: 'none',
  color: '#fff',
  fontSize: '1rem',
  textTransform: 'none',
  '&:hover': {
    textDecoration: 'underline',
  },
};

const logoutButtonStyle = {
  color: '#fff',
  borderColor: '#fff',
  textTransform: 'none',
  '&:hover': {
    borderColor: '#ddd',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
  },
};

export default Navbar;
