import React, { useState, useEffect } from 'react';
import { Grid, Card, CardContent, Typography, styled, Box, Container, Button, TextField, Dialog, DialogActions, DialogContent, DialogTitle, Snackbar, Alert } from '@mui/material';
import axios from '../features/axios';
import { useSelector } from 'react-redux';
import dayjs from 'dayjs'; // Import dayjs for date formatting
import 'dayjs/locale/fr'; // Import French locale for dayjs
dayjs.locale('fr'); // Set dayjs locale to French


const StyledCard = styled(Card)(({ theme }) => ({
    border: '1px solid #ddd',
    borderRadius: theme.spacing(1),
    boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
    transition: 'transform 0.2s',
    '&:hover': {
        transform: 'translateY(-2px)',
    },
}));

const Dashboard = () => {
    const user = useSelector((state) => state.user);
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [openModal, setOpenModal] = useState(false);
    const [newReservation, setNewReservation] = useState({
        description: '',
        date: dayjs().format('YYYY-MM-DD'), // Default to today's date
        time: '09:00', // Default time
        department: '', // Add department field
    });
    const [snackbarOpen, setSnackbarOpen] = useState(false);
    const [snackbarMessage, setSnackbarMessage] = useState('');
    const [snackbarSeverity, setSnackbarSeverity] = useState('info');



    useEffect(() => {
        const fetchReservations = async () => {
            setLoading(true);
            setError(null);

            try {
                if (user && user.name) {
                    const response = await axios.get('/reservations/user', {
                        params: { name: user.name },
                    });
                    setReservations(response.data);
                } else {
                    setError("User not logged in or name not available.");
                }
            } catch (err) {
                console.error("Error fetching reservations:", err);
                setError("Error fetching reservations. Please try again later.");
            } finally {
                setLoading(false);
            }
        };

        fetchReservations();
    }, [user]);

    const handleOpenModal = () => {
        setOpenModal(true);
    };

    const handleCloseModal = () => {
        setOpenModal(false);
        setNewReservation({
            description: '',
            date: dayjs().format('YYYY-MM-DD'),
            time: '09:00',
            department: '',
        }); // Reset form on close
        setError(null); // Clear any previous errors
    };

    const handleInputChange = (event) => {
        setNewReservation({ ...newReservation, [event.target.name]: event.target.value });
    };

    const handleCreateReservation = async () => {
        setError(null); // Clear previous errors
        try {
            const response = await axios.post('/api/reservations', newReservation);

            setReservations([...reservations, response.data]); // Update reservations list
            handleCloseModal();
            setSnackbarMessage('Reservation created successfully!');
            setSnackbarSeverity('success');
            setSnackbarOpen(true);

        } catch (error) {
            console.error("Error creating reservation:", error);
            setError(error.response?.data?.error || "Error creating reservation. Please try again.");
            setSnackbarMessage(error.response?.data?.error || "Error creating reservation. Please try again.");
            setSnackbarSeverity('error');
            setSnackbarOpen(true);
        }
    };

    const handleSnackbarClose = (event, reason) => {
        if (reason === 'clickaway') {
            return;
        }
        setSnackbarOpen(false);
    };


    if (loading) {
        return <Typography variant="h6">Loading reservations...</Typography>;
    }

    if (error) {
        return <Typography variant="h6" color="error">{error}</Typography>;
    }

    return (
        <Box sx={{ mt: 4 }}>
            <Container maxWidth="lg">
                <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', mb: 4 }}>
                    Dashboard
                </Typography>
                <Button variant="contained" color="primary" onClick={handleOpenModal} sx={{ mb: 2 }}>
                    Create Reservation
                </Button>
                <Grid container spacing={3}>
                    {reservations.map((reservation) => (
                        <Grid item key={reservation.id} xs={12} sm={6} md={4} lg={3}>
                            <StyledCard>
                                <CardContent>
                                    <Typography variant="h6" component="h2" gutterBottom>
                                        {reservation.description}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        {dayjs(reservation.date).format('LL')} {reservation.time} {/* Format date with dayjs */}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        {reservation.department}
                                    </Typography>
                                </CardContent>
                            </StyledCard>
                        </Grid>
                    ))}
                </Grid>
            </Container>

            {/* Reservation Modal */}
            <Dialog open={openModal} onClose={handleCloseModal}>
                <DialogTitle>Create New Reservation</DialogTitle>
                <DialogContent>
                    <TextField
                        fullWidth
                        label="Description"
                        name="description"
                        value={newReservation.description}
                        onChange={handleInputChange}
                        margin="normal"
                    />
                    <TextField
                        fullWidth
                        label="Date"
                        type="date"
                        name="date"
                        value={newReservation.date}
                        onChange={handleInputChange}
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}
                    />
                    <TextField
                        fullWidth
                        label="Time"
                        type="time"
                        name="time"
                        value={newReservation.time}
                        onChange={handleInputChange}
                        margin="normal"
                        InputLabelProps={{
                            shrink: true,
                        }}
                    />
                    <TextField
                        fullWidth
                        label="Department"
                        name="department"
                        value={newReservation.department}
                        onChange={handleInputChange}
                        margin="normal"
                    />
                    {error && <Typography color="error">{error}</Typography>} {/* Display error message */}
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleCloseModal}>Cancel</Button>
                    <Button onClick={handleCreateReservation} variant="contained" color="primary">
                        Create
                    </Button>
                </DialogActions>
            </Dialog>

            {/* Snackbar for notifications */}
            <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleSnackbarClose}>
                <Alert onClose={handleSnackbarClose} severity={snackbarSeverity}>
                    {snackbarMessage}
                </Alert>
            </Snackbar>

        </Box>
    );
};

export default Dashboard;
