import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import dayjs from 'dayjs';

const Dashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [departments, setDepartments] = useState([]);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('info');
  
  const [newReservation, setNewReservation] = useState({
    description: '',
    date: dayjs().format('YYYY-MM-DD'),
    time: '09:00',
    department: '',
    phone: '',
  });

  useEffect(() => {
    const fetchReservations = async () => {
      setLoading(true);
      try {
        if (user?.name) {
          const response = await fetch('http://127.0.0.1:5000/api/reservations/user?name=' + user.name);
          const data = await response.json();
          setReservations(data);
        } else {
          setError('User not logged in or name not available.');
        }
      } catch (err) {
        setError('Error fetching reservations. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchReservations();
  }, [user]);

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await fetch('/api/departments');
        const data = await response.json();
        setDepartments(data.map(dept => ({
          ...dept,
          nomdepart: decodeURIComponent(escape(dept.nomdepart))
        })));
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };

    fetchDepartments();
  }, []);

  const handleCreateReservation = async () => {
    try {
      const response = await fetch('/api/reservations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...newReservation,
          citizen: user.name,
          status: 'En attente',
        }),
      });

      if (!response.ok) throw new Error('Failed to create reservation');

      const data = await response.json();
      setReservations([...reservations, data]);
      setOpenModal(false);
      setAlertMessage('Reservation created successfully!');
      setAlertType('success');
      setShowAlert(true);
    } catch (error) {
      setError(error.message);
      setAlertMessage(error.message);
      setAlertType('error');
      setShowAlert(true);
    }
  };

  if (loading) {
    return <div className="text-xl text-center mt-8">Loading reservations...</div>;
  }

  if (error) {
    return <div className="text-xl text-red-500 text-center mt-8">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 mt-8">
      <h1 className="text-3xl font-bold text-center mb-8">Dashboard</h1>

      {(!reservations || reservations.length === 0) ? (
        <div className="text-center p-8 bg-white rounded-lg shadow-md">
          <h2 className="text-xl mb-4">No reservations found.</h2>
          <button 
            onClick={() => setOpenModal(true)}
            className="bg-blue-500 text-white px-4 py-2 rounded hover:bg-blue-600 transition-colors"
          >
            Create Reservation
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {reservations.map((reservation) => (
            <div key={reservation.id} className="bg-white rounded-lg shadow-md p-6">
              <div className="mb-4">
                <h2 className="text-xl font-semibold mb-4">Reservation Details</h2>
                <div className="space-y-4">
                  <div>
                    <p className="text-sm text-gray-500">Description:</p>
                    <p>{reservation.description}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Date:</p>
                    <p>{reservation.date}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Time:</p>
                    <p>{reservation.time}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Department:</p>
                    <p>{reservation.department}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Phone:</p>
                    <p>{reservation.phone}</p>
                  </div>
                  <span 
                    className={`inline-block px-2 py-1 rounded-full text-sm ${
                      reservation.status === 'En attente' 
                        ? 'bg-yellow-100 text-yellow-800' 
                        : 'bg-green-100 text-green-800'
                    }`}
                  >
                    {reservation.status}
                  </span>
                </div>
                <div className="flex justify-end space-x-2 mt-4">
                  <button className="px-4 py-2 border border-gray-300 rounded hover:bg-gray-50 transition-colors">
                    Edit
                  </button>
                  <button className="px-4 py-2 bg-red-500 text-white rounded hover:bg-red-600 transition-colors">
                    Delete
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}

      {openModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4">
          <div className="bg-white rounded-lg p-6 max-w-md w-full">
            <h2 className="text-xl font-semibold mb-4">Create New Reservation</h2>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Description"
                className="w-full p-2 border rounded"
                value={newReservation.description}
                onChange={(e) => setNewReservation({
                  ...newReservation,
                  description: e.target.value
                })}
              />
              <input
                type="date"
                className="w-full p-2 border rounded"
                value={newReservation.date}
                onChange={(e) => setNewReservation({
                  ...newReservation,
                  date: e.target.value
                })}
              />
              <input
                type="time"
                className="w-full p-2 border rounded"
                value={newReservation.time}
                onChange={(e) => setNewReservation({
                  ...newReservation,
                  time: e.target.value
                })}
              />
              <select
                className="w-full p-2 border rounded"
                value={newReservation.department}
                onChange={(e) => setNewReservation({
                  ...newReservation,
                  department: e.target.value
                })}
              >
                <option value="">Select department</option>
                {departments.map((department) => (
                  <option key={department.id} value={department.nomdepart}>
                    {department.nomdepart}
                  </option>
                ))}
              </select>
              <input
                type="text"
                placeholder="Phone number"
                className="w-full p-2 border rounded"
                value={newReservation.phone}
                onChange={(e) => setNewReservation({
                  ...newReservation,
                  phone: e.target.value
                })}
              />
            </div>
            <div className="flex justify-end space-x-2 mt-6">
              <button
                className="px-4 py-2 border border-gray-300 rounded hover:bg-gray-50 transition-colors"
                onClick={() => setOpenModal(false)}
              >
                Cancel
              </button>
              <button
                className="px-4 py-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
                onClick={handleCreateReservation}
              >
                Create
              </button>
            </div>
          </div>
        </div>
      )}

      {showAlert && (
        <div 
          className={`fixed bottom-4 right-4 p-4 rounded-lg shadow-lg ${
            alertType === 'error' ? 'bg-red-100 text-red-800' : 'bg-green-100 text-green-800'
          }`}
        >
          {alertMessage}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
