import React from 'react';
import { Box, Container, Grid, Typography, Button } from '@mui/material';
import { Link } from 'react-router-dom';

const Navbar = () => {
  return (
    <Box sx={{ backgroundColor: '#232323', color: '#fff', py: 2, boxShadow: 2 }}>
      <Container maxWidth="lg">
        <Grid container spacing={4} alignItems="center">
          {/* Left section with the logo/title */}
          <Grid item xs={12} sm={6} md={4}>
            <Typography 
              variant="h5" 
              component="h2" 
              fontFamily="'Roboto', sans-serif" 
              fontWeight="500"
              sx={{ textAlign: { xs: 'center', sm: 'left' } }}
            >
              Tribunal de Commerce à Casablanca
            </Typography>
          </Grid>

          {/* Centered navigation links */}
          <Grid item xs={12} sm={6} md={4}>
            <Grid container justifyContent="center" spacing={3}>
              <Grid item>
                <Link to="/" style={linkStyle}>Accueil</Link>
              </Grid>
              <Grid item>
                <Link to="/login" style={linkStyle}>Connexion</Link>
              </Grid>
              <Grid item>
                <Link to="/register" style={linkStyle}>Inscription</Link>
              </Grid>
            </Grid>
          </Grid>

          {/* Right section with buttons */}
          <Grid item xs={12} sm={6} md={4}>
            <Grid container justifyContent="flex-end" spacing={2}>
              <Grid item>
                <Button variant="contained" color="success" sx={{ fontSize: '0.875rem' }}>
                  Contactez-nous
                </Button>
              </Grid>
              <Grid item>
                <Button variant="contained" color="error" sx={{ fontSize: '0.875rem' }}>
                  À propos de nous
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

// Link style for consistency
const linkStyle = {
  textDecoration: 'none',
  color: '#fff',
  fontSize: '1rem',
  '&:hover': {
    textDecoration: 'underline',
  }
};

export default Navbar;
