import React from 'react';
import { Grid, Card, CardContent, Typography, styled, Box, Container } from '@mui/material';
import axios from '../axios'; // Make sure the path is correct
import { useSelector } from 'react-redux';

// Styled Components (same as before)
const StyledCard = styled(Card)(({ theme }) => ({ /* ... */ }));

const Dashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = React.useState([]);
  const [loading, setLoading] = React.useState(true); // Add loading state
  const [error, setError] = React.useState(null); // Add error state

  React.useEffect(() => {
    const fetchReservations = async () => {
      setLoading(true); // Set loading to true before fetching
      setError(null);    // Clear any previous errors

      try {
        if (user && user.name) { // Check if user exists and has a name
          const response = await axios.get('/api/reservations/user', {
            params: { username: user.name },
          });
          setReservations(response.data);
        } else {
          setError("User not logged in or name not available."); // Handle case when user info is missing
        }
      } catch (err) {
        console.error("Error fetching reservations:", err);
        setError("Error fetching reservations. Please try again later."); // Set error message
      } finally {
        setLoading(false); // Set loading to false after fetch, regardless of success/failure
      }
    };

    fetchReservations(); // Call the async function
  }, [user]); // Add user as a dependency to the useEffect

  if (loading) {
    return <Typography variant="h6">Loading reservations...</Typography>; // Display loading message
  }

  if (error) {
    return <Typography variant="h6" color="error">{error}</Typography>; // Display error message
  }

  if (!reservations || reservations.length === 0) {
    return <Typography variant="h6">No reservations found.</Typography>; // Display no reservations message
  }

  return (
    <Box sx={{ mt: 4 }}>
      <Container maxWidth="lg">
        <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', mb: 4 }}>
          Dashboard
        </Typography>
        <Grid container spacing={3}>
          {reservations.map((reservation) => (
            <Grid item key={reservation.id} xs={12} sm={6} md={4} lg={3}>
              <StyledCard>
                <CardContent>
                  <Typography variant="h6" component="h2" gutterBottom>
                    {reservation.description}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {reservation.date} {reservation.time}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {reservation.department}
                  </Typography>
                </CardContent>
              </StyledCard>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default Dashboard;
