import React from 'react';
import { Grid, Card, CardContent, Typography, styled } from '@mui/material';
import axios from '../axios';

// Styled Components for better styling
const StyledCard = styled(Card)(({ theme }) => ({
  border: '1px solid #ddd', // Add a border
  borderRadius: theme.spacing(1),
  boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  transition: 'transform 0.2s', // Add a hover transition
  '&:hover': {
    transform: 'translateY(-2px)', // Lift the card on hover
  },
}));

const Dashboard = () => {
  const [reservations, setReservations] = React.useState();

  React.useEffect(() => {
    // Fetch user-specific reservations
    axios.get('/api/reservations/user', { 
      params: { username: 'testuser' } // Replace 'testuser' with actual username from your auth
    })
  .then((response) => {
      setReservations(response.data);
    })
  .catch((error) => {
      console.error(error);
    });
  },);

  return (
    <Box sx={{ mt: 4 }}> {/* Add some top margin */}
      <Container maxWidth="lg">
        <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', mb: 4 }}>
          Dashboard
        </Typography>

        <Grid container spacing={3}>
          {reservations.map((reservation) => (
            <Grid item key={reservation.id} xs={12} sm={6} md={4} lg={3}>
              <StyledCard>
                <CardContent>
                  <Typography variant="h6" component="h2" gutterBottom>
                    {reservation.description}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {reservation.date} {reservation.time}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {reservation.department}
                  </Typography>
                </CardContent>
              </StyledCard>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default Dashboard;
