import React, { useState } from "react";
import { Button, TextField, Container, Typography } from "@mui/material";
import { useNavigate } from "react-router-dom";

function LoginPage({ onLogin }) {
  // Déclaration des états pour le nom d'utilisateur et le mot de passe
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  
  // Utilisation du hook useNavigate pour rediriger l'utilisateur après la connexion
  const navigate = useNavigate();

  // Fonction pour gérer la connexion
  const handleLogin = () => {
    // Vérification des identifiants
    if (username === "employee" && password === "password") {
      onLogin(username); // Transmettre le nom d'utilisateur au parent
      navigate("/reservations"); // Rediriger vers la page des réservations
    } else {
      alert("Identifiants invalides !"); // Afficher un message d'erreur en cas d'identifiants incorrects
    }
  };

  return (
    <Container>
      {/* Titre de la page de connexion */}
      <Typography variant="h4" gutterBottom>
        Connexion
      </Typography>
      
      {/* Champ pour le nom d'utilisateur */}
      <TextField
        label="Nom d'utilisateur"
        fullWidth
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        margin="normal"
      />
      
      {/* Champ pour le mot de passe */}
      <TextField
        label="Mot de passe"
        type="password"
        fullWidth
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        margin="normal"
      />
      
      {/* Bouton de connexion */}
      <Button variant="contained" onClick={handleLogin}>
        Se connecter
      </Button>
    </Container>
  );
}

export default LoginPage;
