import React from 'react';
import { Grid, Card, CardContent, Typography, styled, Box, Container } from '@mui/material';
import axios from '../axios'; 
import { useSelector } from 'react-redux';

const StyledCard = styled(Card)(({ theme }) => ({
  border: '1px solid #ddd', 
  borderRadius: theme.spacing(1),
  boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  transition: 'transform 0.2s', 
  '&:hover': {
    transform: 'translateY(-2px)', 
  },
}));

const Dashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = React.useState([]);
  const [loading, setLoading] = React.useState(true); 
  const [error, setError] = React.useState(null);

  React.useEffect(() => {
    const fetchReservations = async () => {
      setLoading(true); 
      setError(null);    

      try {
        if (user && user.name) { 
          const response = await axios.get('/api/reservations/user', {
            params: { username: user.name },
          });
          setReservations(response.data);
        } else {
          setError("User not logged in or name not available.");
        }
      } catch (err) {
        console.error("Error fetching reservations:", err);
        setError("Error fetching reservations. Please try again later."); 
      } finally {
        setLoading(false); 
      }
    };

    fetchReservations(); 
  }, [user]); 

  if (loading) {
    return <Typography variant="h6">Loading reservations...</Typography>;
  }

  if (error) {
    return <Typography variant="h6" color="error">{error}</Typography>;
  }

  if (!reservations || reservations.length === 0) {
    return <Typography variant="h6">No reservations found.</Typography>; 
  }

  return (
    <Box sx={{ mt: 4 }}>
      <Container maxWidth="lg">
        <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', mb: 4 }}>
          Dashboard
        </Typography>
        <Grid container spacing={3}>
          {reservations.map((reservation) => (
            <Grid item key={reservation.id} xs={12} sm={6} md={4} lg={3}>
              <StyledCard>
                <CardContent>
                  <Typography variant="h6" component="h2" gutterBottom>
                    {reservation.description}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {reservation.date} {reservation.time}
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {reservation.department}
                  </Typography>
                </CardContent>
              </StyledCard>
            </Grid>
          ))}
        </Grid>
      </Container>
    </Box>
  );
};

export default Dashboard;
