import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Grid,
  Box,
  Card,
  CardContent,
  Button,
  Chip,
} from "@mui/material";
import { motion } from "framer-motion";
import axios from "axios";
import { DateTime } from "luxon"; // Date formatting library
import Calendar from "react-calendar"; // Calendar component
import "react-calendar/dist/Calendar.css"; // Calendar styles

function ReservationList() {
  const [reservations, setReservations] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [allReservations, setAllReservations] = useState([]);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await axios.get(
          "http://127.0.0.1:5000/api/reservations"
        );
        setAllReservations(response.data);
        setReservations(response.data); // Initially show all reservations
      } catch (error) {
        alert("Erreur lors de la récupération des réservations.");
      }
    };

    fetchReservations();
  }, []);

  // Filter reservations based on selected date
  const filteredReservations = reservations.filter((reservation) => {
    const reservationDate = DateTime.fromISO(reservation.date);
    return reservationDate.hasSame(DateTime.fromJSDate(selectedDate), 'day');
  });

  // Customize the calendar tile style (optional)
  const tileClassName = ({ date, view }) => {
    // Add a custom style for the current day
    if (view === "month" && isReservedDate(date)) {
      return "reserved-tile";
    }
  };

  const isReservedDate = (date) => {
    const selectedDate = DateTime.fromJSDate(date);
    return reservations.some((reservation) =>
      DateTime.fromISO(reservation.date).hasSame(selectedDate, "day")
    );
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom align="center" sx={{ fontWeight: "bold", marginBottom: "20px" }}>
        Liste des Réservations
      </Typography>

      {/* Calendar */}
      <Box sx={{ display: "flex", justifyContent: "center", marginBottom: "30px" }}>
        <motion.div whileHover={{ scale: 1.05 }}>
          <Calendar
            onChange={setSelectedDate}
            value={selectedDate}
            tileClassName={tileClassName}
          />
        </motion.div>
      </Box>

      {/* Reservation List */}
      <Grid container spacing={3}>
        {filteredReservations.length === 0 ? (
          <Typography variant="h6" color="textSecondary" align="center" sx={{ width: "100%" }}>
            Aucune réservation pour cette date.
          </Typography>
        ) : (
          filteredReservations.map((reservation, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <motion.div whileHover={{ scale: 1.05 }}>
                <Card sx={{ display: "flex", flexDirection: "column", boxShadow: 3, borderRadius: 2 }}>
                  <CardContent>
                    <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                      {reservation.space} - {reservation.resource}
                    </Typography>
                    <Typography variant="body2" color="textSecondary" sx={{ marginBottom: "10px" }}>
                      {`Le ${DateTime.fromISO(reservation.date).toLocaleString(DateTime.DATE_FULL)} à ${DateTime.fromISO(reservation.time).toLocaleString(DateTime.TIME_SIMPLE)}`}
                    </Typography>
                    <Chip label="Réservé" color="primary" />
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))
        )}
      </Grid>

      {/* All reservations made by the user */}
      <Typography variant="h5" sx={{ marginTop: "40px", marginBottom: "20px" }}>
        Toutes mes réservations
      </Typography>
      <Grid container spacing={3}>
        {allReservations.map((reservation, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <motion.div whileHover={{ scale: 1.05 }}>
              <Card sx={{ boxShadow: 3, borderRadius: 2 }}>
                <CardContent>
                  <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                    {reservation.space} - {reservation.resource}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    {`Le ${DateTime.fromISO(reservation.date).toLocaleString(DateTime.DATE_FULL)} à ${DateTime.fromISO(reservation.time).toLocaleString(DateTime.TIME_SIMPLE)}`}
                  </Typography>
                  <Chip label="Réservé" color="primary" sx={{ marginTop: "10px" }} />
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default ReservationList;
