import React, { useEffect, useState } from "react";
import {
  Container,
  Typography,
  Grid,
  Box,
  Card,
  CardContent,
  Button,
  Chip,
} from "@mui/material";
import axios from "axios";
import { DateTime } from "luxon"; // Date formatting library
import Calendar from "react-calendar"; // Calendar component
import "react-calendar/dist/Calendar.css"; // Calendar styles
import { motion } from "framer-motion";

function ReservationList() {
  const [reservations, setReservations] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [allReservations, setAllReservations] = useState([]);

  useEffect(() => {
    const fetchReservations = async () => {
      try {
        const response = await axios.get(
          "http://127.0.0.1:5000/api/reservations"
        );
        setAllReservations(response.data);
        setReservations(response.data); // Initially show all reservations
      } catch (error) {
        alert("Erreur lors de la récupération des réservations.");
      }
    };

    fetchReservations();
  }, []);

  // Filter reservations based on selected date
  const filteredReservations = reservations.filter((reservation) => {
    const reservationDate = DateTime.fromISO(reservation.date);
    return reservationDate.hasSame(DateTime.fromJSDate(selectedDate), "day");
  });

  // Customize the calendar tile style
  const tileClassName = ({ date, view }) => {
    if (view === "month" && isReservedDate(date)) {
      return "reserved-tile";
    }
  };

  const isReservedDate = (date) => {
    const selectedDate = DateTime.fromJSDate(date);
    return reservations.some((reservation) =>
      DateTime.fromISO(reservation.date).hasSame(selectedDate, "day")
    );
  };

  return (
    <Container sx={{ py: 4, textAlign: "center" }}>
      <Typography
        variant="h3"
        gutterBottom
        component={motion.div}
        initial={{ opacity: 0, y: -20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        sx={{ fontWeight: "bold", color: "#1976d2", mb: 4 }}
      >
        Liste des Réservations
      </Typography>

      {/* Calendar */}
      <Box sx={{ display: "flex", justifyContent: "center", mb: 4 }}>
        <motion.div initial={{ scale: 0.9 }} animate={{ scale: 1 }} transition={{ duration: 0.3 }}>
          <Calendar
            onChange={setSelectedDate}
            value={selectedDate}
            tileClassName={tileClassName}
          />
        </motion.div>
      </Box>

      {/* Reservation List */}
      <Grid container spacing={3}>
        {filteredReservations.length === 0 ? (
          <Typography
            variant="h6"
            color="textSecondary"
            align="center"
            sx={{ width: "100%", opacity: 0.8 }}
          >
            Aucune réservation pour cette date.
          </Typography>
        ) : (
          filteredReservations.map((reservation, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <motion.div whileHover={{ scale: 1.05 }}>
                <Card
                  sx={{
                    display: "flex",
                    flexDirection: "column",
                    boxShadow: 4,
                    borderRadius: 3,
                    overflow: "hidden",
                  }}
                >
                  <CardContent>
                    <Typography
                      variant="h6"
                      sx={{ fontWeight: "bold", color: "#2c387e" }}
                    >
                      {reservation.space} - {reservation.resource}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="textSecondary"
                      sx={{ mb: 2 }}
                    >
                      {`Le ${DateTime.fromISO(reservation.date).toLocaleString(
                        DateTime.DATE_FULL
                      )} à ${DateTime.fromISO(reservation.time).toLocaleString(
                        DateTime.TIME_SIMPLE
                      )}`}
                    </Typography>
                    <Chip label="Réservé" color="primary" sx={{ mt: 1 }} />
                  </CardContent>
                </Card>
              </motion.div>
            </Grid>
          ))
        )}
      </Grid>

      {/* All reservations made by the user */}
      <Typography
        variant="h4"
        sx={{ mt: 6, mb: 3, fontWeight: "bold", color: "#1976d2" }}
      >
        Toutes mes réservations
      </Typography>
      <Grid container spacing={3}>
        {allReservations.map((reservation, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <motion.div whileHover={{ scale: 1.05 }}>
              <Card sx={{ boxShadow: 4, borderRadius: 3 }}>
                <CardContent>
                  <Typography
                    variant="h6"
                    sx={{ fontWeight: "bold", color: "#2c387e" }}
                  >
                    {reservation.space} - {reservation.resource}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    {`Le ${DateTime.fromISO(reservation.date).toLocaleString(
                      DateTime.DATE_FULL
                    )} à ${DateTime.fromISO(reservation.time).toLocaleString(
                      DateTime.TIME_SIMPLE
                    )}`}
                  </Typography>
                  <Chip
                    label="Réservé"
                    color="primary"
                    sx={{ mt: 1 }}
                  />
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default ReservationList;
