import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  styled,
  Box,
  Container,
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogTitle,
  Snackbar,
  Alert,
  Paper,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
} from '@mui/material';
import axios from '../features/axios';
import { useSelector } from 'react-redux';
import dayjs from 'dayjs';
import 'dayjs/locale/fr';
dayjs.locale('fr');

const StyledCard = styled(Card)(({ theme }) => ({
  border: '1px solid #ddd',
  borderRadius: theme.spacing(1),
  boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)',
  transition: 'transform 0.2s',
  display: 'flex',
  flexDirection: 'row', // Horizontal layout
  alignItems: 'center', // Vertically center content
}));

const decodeText = (text) => decodeURIComponent(escape(text));

const Dashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [newReservation, setNewReservation] = useState({
    description: '',
    date: dayjs().format('YYYY-MM-DD'),
    time: '09:00',
    department: '',
    phone: '',
  });
  const [departments, setDepartments] = useState([]);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('info');

  useEffect(() => {
    const fetchReservations = async () => {
      setLoading(true);
      setError(null);

      try {
        if (user && user.name) {
          const response = await axios.get('/reservations/user', {
            params: { name: user.name },
          });
          setReservations(response.data);
        } else {
          setError('User not logged in or name not available.');
        }
      } catch (err) {
        console.error('Error fetching reservations:', err);
        setError('Error fetching reservations. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchReservations();
  }, [user]);

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await axios.get('/departments');
        const decodedDepartments = response.data.map((dept) => ({
          ...dept,
          nomdepart: decodeText(dept.nomdepart),
        }));
        setDepartments(decodedDepartments);
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };

    fetchDepartments();
  }, []);

  const handleOpenModal = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setNewReservation({
      description: '',
      date: dayjs().format('YYYY-MM-DD'),
      time: '09:00',
      department: '',
    });
    setError(null);
  };

  const handleInputChange = (event) => {
    setNewReservation({ ...newReservation, [event.target.name]: event.target.value });
  };

  const handleCreateReservation = async () => {
    setError(null);
    try {
      const reservationData = {
        ...newReservation,
        citizen: user.name,
        status: 'En attente',
      };

      const response = await axios.post('/reservations', reservationData);

      setReservations([...reservations, response.data]);
      handleCloseModal();
      setSnackbarMessage('Reservation created successfully!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
    } catch (error) {
      console.error('Error creating reservation:', error);
      setError(error.response?.data?.error || 'Error creating reservation. Please try again.');
      setSnackbarMessage(
        error.response?.data?.error || 'Error creating reservation. Please try again.'
      );
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleSnackbarClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setSnackbarOpen(false);
  };

  if (loading) {
    return <Typography variant="h6">Loading reservations...</Typography>;
  }

  if (error) {
    return (
      <Typography variant="h6" color="error">
        {error}
      </Typography>
    );
  }

  return (
    <Box sx={{ mt: 4 }}>
      <Container maxWidth="lg">
        <Typography
          variant="h4"
          component="h1"
          gutterBottom
          sx={{ fontWeight: 'bold', mb: 4, textAlign: 'center' }}
        >
          Dashboard
        </Typography>

        {!reservations || reservations.length === 0 ? (
          <Paper elevation={3} sx={{ padding: 4, textAlign: 'center' }}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              No reservations found.
            </Typography>
            <Button variant="contained" color="primary" onClick={handleOpenModal}>
              Create Reservation
            </Button>
          </Paper>
        ) : (
          <Grid container spacing={3}>
            {reservations.map((reservation) => (
              <Grid item key={reservation.id} xs={12} sm={6} md={4} lg={3} xl={2}>
                <StyledCard elevation={3}>
                  <CardContent sx={{ flex: 1 }}>
                    <Typography variant="h6" component="div" gutterBottom>
                      Reservation Details
                    </Typography>
                    <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                      <Box mb={1}>
                        <Typography variant="body2" color="text.secondary">
                          Description:
                        </Typography>
                        <Typography variant="body1">{reservation.description}</Typography>
                      </Box>
                      <Box mb={1}>
                        <Typography variant="body2" color="text.secondary">
                          Date:
                        </Typography>
                        <Typography variant="body1">{reservation.date}</Typography>
                      </Box>
                      <Box mb={1}>
                        <Typography variant="body2" color="text.secondary">
                          Time:
                        </Typography>
                        <Typography variant="body1">{reservation.time}</Typography>
                      </Box>
                      <Box mb={1}>
                        <Typography variant="body2" color="text.secondary">
                          Department:
                        </Typography>
                        <Typography variant="body1">{reservation.department}</Typography>
                      </Box>
                      <Box mb={1}>
                        <Typography variant="body2" color="text.secondary">
                          Citizen:
                        </Typography>
                        <Typography variant="body1">{reservation.citizen}</Typography>
                      </Box>
                      <Box mb={1}>
                        <Typography variant="body2" color="text.secondary">
                          Phone:
                        </Typography>
                        <Typography variant="body1">{reservation.phone}</Typography>
                      </Box>
                    </Box>
                    <Chip
              label={`Status: ${reservation.status}`}
              color={reservation.status === 'En attente' ? 'warning' : 'success'}
              size="small"
              sx={{ mt: 2 }}
            />
                  </CardContent>
                  <CardContent
                    sx={{
                      flex: 1,
                      display: 'flex',
                      flexDirection: 'column',
                      alignItems: 'flex-end',
                      justifyContent: 'center',
                    }}
                  >
                    <Button variant="outlined" color="primary" sx={{ m: 1 }}>
                      Edit
                    </Button>
                    <Button variant="contained" color="error" sx={{ m: 1 }}>
                      Delete
                    </Button>
                  </CardContent>
                </StyledCard>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>

      <Dialog open={openModal} onClose={handleCloseModal}>
        <DialogTitle>Create New Reservation</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Description"
            name="description"
            value={newReservation.description}
            onChange={handleInputChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Date"
            type="date"
            name="date"
            value={newReservation.date}
            onChange={handleInputChange}
            margin="normal"
            InputLabelProps={{
              shrink: true,
            }}
          />
          <TextField
            fullWidth
            label="Time"
            type="time"
            name="time"
            value={newReservation.time}
            onChange={handleInputChange}
            margin="normal"
            InputLabelProps={{
              shrink: true,
            }}
          />
          <FormControl fullWidth margin="normal">
            <InputLabel id="department-label">Department</InputLabel>
            <Select
              labelId="department-label"
              id="department"
              name="department"
              value={newReservation.department}
              label="Department"
              onChange={handleInputChange}
            >
              {departments.map((department) => (
                <MenuItem key={department.id} value={department.nomdepart}>
                  {department.nomdepart}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <TextField
            fullWidth
            label="Phone Number"
            name="phone"
            value={newReservation.phone}
            onChange={handleInputChange}
            margin="normal"
          />
          {error && <Typography color="error">{error}</Typography>}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseModal}>Cancel</Button>
          <Button onClick={handleCreateReservation} variant="contained" color="primary">
            Create
          </Button>
        </DialogActions>
      </Dialog>

      <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={handleSnackbarClose}>
        <Alert onClose={handleSnackbarClose} severity={snackbarSeverity}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Dashboard;
