import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { AppBar, Toolbar, Typography, Button, Container, IconButton, Drawer, List, ListItem, ListItemText } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import LoginPage from "./components/LoginPage";
import ReservationForm from "./components/ReservationForm";
import ReservationList from "./components/ReservationList";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userName, setUserName] = useState("");
  const [openSidebar, setOpenSidebar] = useState(false);

  // Handle login (just a placeholder, replace with actual logic)
  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUserName(username);
  };

  // Handle logout
  const handleLogout = () => {
    setIsLoggedIn(false);
    setUserName("");
  };

  // Handle sidebar toggle
  const toggleSidebar = () => {
    setOpenSidebar(!openSidebar);
  };

  return (
    <Router>
      <div>
        {/* Navigation Bar */}
        <AppBar position="sticky">
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              My Reservation App
            </Typography>
            {!isLoggedIn ? (
              <Button color="inherit" component={Link} to="/" onClick={() => setOpenSidebar(false)}>
                Login
              </Button>
            ) : (
              <div>
                <Button color="inherit" onClick={toggleSidebar}>
                  <MenuIcon />
                </Button>
                <Button color="inherit" onClick={handleLogout}>
                  Logout
                </Button>
                <span style={{ marginLeft: "10px" }}>{userName}</span>
              </div>
            )}
          </Toolbar>
        </AppBar>

        {/* Sidebar */}
        <Drawer open={openSidebar} onClose={toggleSidebar}>
          <List>
            <ListItem button component={Link} to="/reservations" onClick={toggleSidebar}>
              <ListItemText primary="Reservations" />
            </ListItem>
            <ListItem button component={Link} to="/reserve" onClick={toggleSidebar}>
              <ListItemText primary="Reserve" />
            </ListItem>
          </List>
        </Drawer>

        {/* Routes */}
        <Container sx={{ marginTop: "20px" }}>
          <Routes>
            <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
            <Route path="/reservations" element={<ReservationList />} />
            <Route path="/reserve" element={<ReservationForm />} />
          </Routes>
        </Container>
      </div>
    </Router>
  );
}

export default App;
