import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Modal } from '@mui/material';
import axios from '../features/axios';
import { useDispatch } from 'react-redux';
import { login } from '../features/actions';
import { useNavigate } from 'react-router-dom';
import { styled } from '@mui/material/styles';

// Styled Components
const RegisterBox = styled(Box)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
  height: '100vh',
  backgroundColor: '#f4f6f9',
}));

const FormBox = styled(Box)(({ theme }) => ({
  padding: theme.spacing(3),
  backgroundColor: 'white',
  borderRadius: 2,
  boxShadow: 3,
  width: '400px',
}));

const Title = styled(Typography)(({ theme }) => ({
  marginBottom: theme.spacing(2),
  textAlign: 'center',
  fontSize: '1.5rem',
  fontWeight: '600',
  color: '#374151',
}));

const StyledTextField = styled(TextField)(({ theme }) => ({
  marginBottom: theme.spacing(2),
  width: '100%',
}));

const RegisterButton = styled(Button)(({ theme }) => ({
  marginBottom: theme.spacing(2),
  width: '100%',
  backgroundColor: '#059669',
  '&:hover': {
    backgroundColor: '#10B981',
  },
}));

const ErrorText = styled(Typography)(({ theme }) => ({
  color: 'red',
  marginBottom: theme.spacing(2),
}));

const SuccessText = styled(Typography)(({ theme }) => ({
  color: 'green',
  textAlign: 'center',
  marginBottom: theme.spacing(2),
}));

const VerificationModal = styled(Modal)(({ theme }) => ({
  display: 'flex',
  justifyContent: 'center',
  alignItems: 'center',
}));

const VerificationBox = styled(Box)(({ theme }) => ({
  padding: theme.spacing(3),
  backgroundColor: 'white',
  borderRadius: 2,
  boxShadow: 3,
  width: '400px',
  marginTop: '20vh',
}));

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [openModal, setOpenModal] = useState(false);
  const [error, setError] = useState(null);
  const [verificationMessage, setVerificationMessage] = useState('');
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('/register', { username, email, password });
      setVerificationMessage('A verification code has been sent to your email. Please check it.');
      setOpenModal(true);

      dispatch(
        login({
          user: response.data.user,
          role: response.data.role,
          token: response.data.token,
        })
      );
    } catch (error) {
      setError(error.response ? error.response.data.error : 'An unexpected error occurred');
    }
  };

  const handleVerifyCode = async () => {
    try {
      const response = await axios.post('/verify', { email, code: verificationCode });
      if (response && response.data) {
        setVerificationMessage('Your email has been successfully verified!');
        const loginResponse = await axios.post('/login', { email, password });
        
        if (loginResponse && loginResponse.data) {
          dispatch(login({
            user: loginResponse.data,
            role: loginResponse.data.role,
            name: loginResponse.data.name,
            numdep: loginResponse.data.numdep,
          }));
          navigate('/');
        } else {
          setError('Login failed: No data returned from server');
        }
      } else {
        setError('Verification failed: No data returned from server');
      }
    } catch (error) {
      setError(error.response ? error.response.data.error : 'An unexpected error occurred');
    }
  };

  return (
    <RegisterBox>
      <FormBox>
        <Title variant="h5">Register</Title>
        <form onSubmit={handleSubmit}>
          <StyledTextField
            label="Username"
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
          />
          <StyledTextField
            label="Email"
            type="email"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
          />
          <StyledTextField
            label="Password"
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
          />
          <StyledTextField
            label="Confirm Password"
            type="password"
            value={confirmPassword}
            onChange={(event) => setConfirmPassword(event.target.value)}
          />
          <RegisterButton type="submit" variant="contained">
            Register
          </RegisterButton>
          {error && <ErrorText>{error}</ErrorText>}
        </form>
        {verificationMessage && <SuccessText>{verificationMessage}</SuccessText>}
      </FormBox>

      <VerificationModal open={openModal} onClose={() => setOpenModal(false)}>
        <VerificationBox>
          <Typography variant="h6" sx={{ marginBottom: 2 }}>Enter Verification Code</Typography>
          <StyledTextField
            label="Verification Code"
            type="text"
            value={verificationCode}
            onChange={(event) => setVerificationCode(event.target.value)}
          />
          <RegisterButton onClick={handleVerifyCode}>Verify</RegisterButton>
          {error && <ErrorText>{error}</ErrorText>}
        </VerificationBox>
      </VerificationModal>
    </RegisterBox>
  );
};

export default Register;
