import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { AppBar, Toolbar, Typography, Button, Container, Box, List, ListItem, ListItemText, IconButton, Paper } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import LoginPage from "./components/LoginPage";
import ReservationForm from "./components/ReservationForm";
import ReservationList from "./components/ReservationList";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
  };

  return (
    <Router>
      <div>
        {/* Navigation Bar */}
        <AppBar position="sticky">
          <Toolbar>
            {/* Menu Icon */}
            {isLoggedIn && (
              <IconButton edge="start" color="inherit">
                <MenuIcon />
              </IconButton>
            )}
            {/* Title */}
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              My Reservation App
            </Typography>

            {/* Login or User Info */}
            {isLoggedIn ? (
              <>
                <Button color="inherit" onClick={handleLogout}>
                  Logout
                </Button>
              </>
            ) : (
              <Button color="inherit" component={Link} to="/">
                Login
              </Button>
            )}
          </Toolbar>
        </AppBar>

        {/* Sidebar Drawer under the Navbar */}
        {isLoggedIn && (
          <Paper elevation={3} sx={{ width: 250, position: "fixed", top: 64, left: 0, height: "100vh" }}>
            <List>
              <ListItem button component={Link} to="/reservations">
                <ListItemText primary="Reservations" />
              </ListItem>
              <ListItem button component={Link} to="/reserve">
                <ListItemText primary="Reserve" />
              </ListItem>
              <ListItem button onClick={handleLogout}>
                <ListItemText primary="Logout" />
              </ListItem>
            </List>
          </Paper>
        )}

        {/* Main Content Area */}
        <Container sx={{ marginTop: "20px", marginLeft: isLoggedIn ? "250px" : 0 }}>
          <Routes>
            <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
            <Route path="/reservations" element={isLoggedIn ? <ReservationList /> : <LoginPage onLogin={handleLogin} />} />
            <Route path="/reserve" element={isLoggedIn ? <ReservationForm /> : <LoginPage onLogin={handleLogin} />} />
          </Routes>
        </Container>
      </div>
    </Router>
  );
}

export default App;
