import React, { useState, useEffect } from 'react';
import { CalendarDays, Clock, Phone, Building2, User, FileText } from 'lucide-react';

const Dashboard = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [departments, setDepartments] = useState([]);
  const [newReservation, setNewReservation] = useState({
    description: '',
    date: new Date().toISOString().split('T')[0],
    time: '09:00',
    department: '',
    phone: ''
  });

  // Status colors for the badges
  const statusColors = {
    'En attente': 'bg-amber-500',
    'Confirmé': 'bg-emerald-500',
    'Annulé': 'bg-red-500'
  };

  const handleInputChange = (e) => {
    setNewReservation({ ...newReservation, [e.target.name]: e.target.value });
  };

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-50 to-orange-50 pb-12">
      {/* Decorative header */}
      <div className="h-4 bg-gradient-to-r from-emerald-600 via-emerald-500 to-emerald-600" />
      
      <div className="max-w-7xl mx-auto px-4 py-8">
        {/* Header Section */}
        <div className="relative mb-12 text-center">
          <h1 className="text-4xl font-bold text-gray-800 mb-4">
            Tableau de Bord des Réservations
          </h1>
          <p className="text-lg text-gray-600 max-w-3xl mx-auto mb-8">
            Gérez vos réservations au Tribunal de Commerce de Casablanca
          </p>
          <button 
            onClick={() => setOpenModal(true)}
            className="bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-3 rounded-lg
                     transform transition hover:scale-105 font-semibold inline-flex items-center gap-2"
          >
            <CalendarDays className="w-5 h-5" />
            Nouvelle Réservation
          </button>
        </div>

        {/* Reservations Grid */}
        <div className="grid gap-6 md:grid-cols-2 lg:grid-cols-3">
          {reservations.map((reservation, index) => (
            <div key={index} 
                 className="bg-white rounded-lg overflow-hidden shadow-lg border-t-4 border-emerald-600
                          transform transition hover:scale-105">
              {/* Card Header */}
              <div className="bg-gray-800 text-white p-4">
                <h3 className="text-xl font-semibold mb-2">Détails de la Réservation</h3>
                <div className={`inline-block px-3 py-1 rounded-full text-sm ${statusColors[reservation.status]}`}>
                  {reservation.status}
                </div>
              </div>

              {/* Card Content */}
              <div className="p-6 space-y-4">
                <div className="flex items-start gap-3">
                  <FileText className="w-5 h-5 text-emerald-600 mt-1" />
                  <div>
                    <p className="text-sm text-gray-500">Description</p>
                    <p className="text-gray-800">{reservation.description}</p>
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <CalendarDays className="w-5 h-5 text-emerald-600" />
                  <div>
                    <p className="text-sm text-gray-500">Date</p>
                    <p className="text-gray-800">{reservation.date}</p>
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <Clock className="w-5 h-5 text-emerald-600" />
                  <div>
                    <p className="text-sm text-gray-500">Heure</p>
                    <p className="text-gray-800">{reservation.time}</p>
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <Building2 className="w-5 h-5 text-emerald-600" />
                  <div>
                    <p className="text-sm text-gray-500">Département</p>
                    <p className="text-gray-800">{reservation.department}</p>
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <User className="w-5 h-5 text-emerald-600" />
                  <div>
                    <p className="text-sm text-gray-500">Citoyen</p>
                    <p className="text-gray-800">{reservation.citizen}</p>
                  </div>
                </div>

                <div className="flex items-center gap-3">
                  <Phone className="w-5 h-5 text-emerald-600" />
                  <div>
                    <p className="text-sm text-gray-500">Téléphone</p>
                    <p className="text-gray-800">{reservation.phone}</p>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Empty State */}
        {(!reservations || reservations.length === 0) && !loading && (
          <div className="bg-white rounded-lg shadow-lg p-8 text-center max-w-2xl mx-auto">
            <CalendarDays className="w-16 h-16 text-emerald-600 mx-auto mb-4" />
            <h3 className="text-xl font-semibold mb-2">Aucune Réservation</h3>
            <p className="text-gray-600 mb-6">
              Vous n'avez pas encore de réservations. Créez votre première réservation maintenant.
            </p>
            <button 
              onClick={() => setOpenModal(true)}
              className="bg-emerald-600 hover:bg-emerald-700 text-white px-6 py-3 rounded-lg
                       transform transition hover:scale-105 font-semibold inline-flex items-center gap-2"
            >
              <CalendarDays className="w-5 h-5" />
              Nouvelle Réservation
            </button>
          </div>
        )}

        {/* Loading State */}
        {loading && (
          <div className="text-center py-12">
            <div className="w-16 h-16 border-4 border-emerald-600 border-t-transparent rounded-full animate-spin mx-auto mb-4" />
            <p className="text-gray-600">Chargement des réservations...</p>
          </div>
        )}

        {/* Error State */}
        {error && (
          <div className="bg-red-50 border-l-4 border-red-500 p-4 rounded-lg max-w-2xl mx-auto">
            <p className="text-red-700">{error}</p>
          </div>
        )}
      </div>

      {/* Modal for new reservation would go here - implementation depends on your modal component */}
    </div>
  );
};

export default Dashboard;
