import React, { useEffect, useState } from "react"; 
import { Container, Typography, Grid, Box, Card, CardContent, Chip } from "@mui/material"; 
import axios from "axios"; 
import { DateTime } from "luxon"; // Date formatting library 
import Calendar from "react-calendar"; // Calendar component 
import "react-calendar/dist/Calendar.css"; // Calendar styles

function ReservationList() { 
  const [reservations, setReservations] = useState([]); 
  const [selectedDate, setSelectedDate] = useState(new Date()); 
  const [allReservations, setAllReservations] = useState([]);

  useEffect(() => { 
    const fetchReservations = async () => { 
      try { 
        const response = await axios.get("http://127.0.0.1:5000/api/reservations"); 
        setAllReservations(response.data); 
        setReservations(response.data); 
      } catch (error) { 
        alert("Erreur lors de la récupération des réservations."); 
      } 
    }; 
    fetchReservations(); 
  }, []);

  const filteredReservations = reservations.filter((reservation) => {
    const reservationDate = DateTime.fromISO(reservation.date);
    return reservationDate.hasSame(DateTime.fromJSDate(selectedDate), 'day');
  });

  const isReservedDate = (date) => {
    const selectedDate = DateTime.fromJSDate(date);
    return reservations.some((reservation) =>
      DateTime.fromISO(reservation.date).hasSame(selectedDate, "day")
    );
  };

  return (
    <Container sx={{ padding: "40px", fontFamily: "Poppins, sans-serif" }}>
      <Typography
        variant="h4"
        gutterBottom
        align="center"
        sx={{
          fontWeight: "bold",
          marginBottom: "20px",
          textAlign: "center", // Center the title
        }}
      >
        Liste des Réservations
      </Typography>

      <Box
        sx={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "flex-start",
          marginBottom: "30px",
        }}
      >
        <Box sx={{ width: "70%" }}>
          {filteredReservations.length === 0 ? (
            <Typography variant="h6" color="textSecondary" align="center">
              Aucune réservation pour cette date.
            </Typography>
          ) : (
            <Box sx={{ display: "flex", flexDirection: "column", gap: "15px" }}>
              {filteredReservations.map((reservation, index) => (
                <Card
                  key={index}
                  sx={{
                    boxShadow: 3,
                    borderRadius: 2,
                    padding: "10px",
                    backgroundColor: "#f4f6f8", // Adding subtle background color for visual similarity
                  }}
                >
                  <CardContent>
                    <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                      {reservation.space} - {reservation.resource}
                    </Typography>
                    <Typography
                      variant="body2"
                      color="textSecondary"
                      sx={{ marginBottom: "10px" }}
                    >
                      {`Le ${DateTime.fromISO(reservation.date).toLocaleString(
                        DateTime.DATE_FULL
                      )} à ${DateTime.fromISO(reservation.time).toLocaleString(
                        DateTime.TIME_SIMPLE
                      )}`}
                    </Typography>
                    <Chip label="Réservé" color="primary" />
                  </CardContent>
                </Card>
              ))}
            </Box>
          )}
        </Box>

        <Box
          sx={{
            width: "28%",
            position: "sticky",
            top: "100px", // Adjust top to make it closer to the title
            border: "1px solid #e0e0e0", // Border around the calendar for styling
            borderRadius: "8px", // Rounded corners
            padding: "10px", // Padding for spacing
            backgroundColor: "#ffffff", // White background for the calendar
          }}
        >
          <Calendar
            onChange={setSelectedDate}
            value={selectedDate}
            tileClassName={({ date, view }) =>
              view === "month" && isReservedDate(date) ? "reserved-tile" : null
            }
          />
        </Box>
      </Box>

      <Typography
        variant="h5"
        sx={{
          marginTop: "40px",
          marginBottom: "20px",
          fontWeight: "bold",
          textAlign: "center", // Center the title
        }}
      >
        Toutes mes réservations
      </Typography>
      <Grid container spacing={3}>
        {allReservations.map((reservation, index) => (
          <Grid item xs={12} sm={6} md={4} key={index}>
            <Card sx={{ boxShadow: 3, borderRadius: 2 }}>
              <CardContent>
                <Typography variant="h6" sx={{ fontWeight: "bold" }}>
                  {reservation.space} - {reservation.resource}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  {`Le ${DateTime.fromISO(reservation.date).toLocaleString(
                    DateTime.DATE_FULL
                  )} à ${DateTime.fromISO(reservation.time).toLocaleString(
                    DateTime.TIME_SIMPLE
                  )}`}
                </Typography>
                <Chip label="Réservé" color="primary" sx={{ marginTop: "10px" }} />
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
}

export default ReservationList;
