import React, { useState } from 'react';
import { TextField, Button, Typography, Box, Modal } from '@mui/material';
import axios from '../features/axios';
import { useDispatch } from 'react-redux';
import { login } from '../features/actions';

const Register = () => {
  const [username, setUsername] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [verificationCode, setVerificationCode] = useState('');
  const [openModal, setOpenModal] = useState(false);
  const [error, setError] = useState(null);
  const [verificationMessage, setVerificationMessage] = useState('');
  const dispatch = useDispatch();

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
      setError('Passwords do not match');
      return;
    }

    try {
      const response = await axios.post('/register', { username, email, password });
      console.log('Response:', response); 
      setVerificationMessage('A verification code has been sent to your email. Please check it.');
      setOpenModal(true);

      dispatch(
        login({
          user: response.data.user,
          role: response.data.role,
          token: response.data.token,
        })
      );
    } catch (error) {
      console.error(error); 
      setError(error.response ? error.response.data.error : 'An unexpected error occurred');
    }
  };

  const handleVerifyCode = async () => {
    try {
      const response = await axios.post('/verify', { email, code: verificationCode });
      setVerificationMessage('Your email has been successfully verified!');
      setTimeout(() => (window.location.href = '/login'), 2000);
    } catch (error) {
      setError(error.response.data.error);
    }
  };

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        height: '100vh',
        backgroundColor: '#f4f6f9',
      }}
    >
      <Box
        sx={{ padding: 3, backgroundColor: 'white', borderRadius: 2, boxShadow: 3, width: '400px' }}
      >
        <Typography variant="h5" sx={{ mb: 2, textAlign: 'center' }}>
          Register
        </Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Username"
            type="text"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            label="Email"
            type="email"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            label="Password"
            type="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
            sx={{ mb: 2 }}
          />
          <TextField
            fullWidth
            label="Confirm Password"
            type="password"
            value={confirmPassword}
            onChange={(event) => setConfirmPassword(event.target.value)}
            sx={{ mb: 2 }}
          />
          <Button fullWidth type="submit" variant="contained" color="primary" sx={{ mb: 2 }}>
            Register
          </Button>
          {error && (
            <Typography color="error" variant="body2" sx={{ mb: 2 }}>
              {error}
            </Typography>
          )}
        </form>
        {verificationMessage && (
          <Typography variant="body2" sx={{ color: 'green', textAlign: 'center', mb: 2 }}>
            {verificationMessage}
          </Typography>
        )}
      </Box>

      {/* Verification Modal */}
      <Modal open={openModal} onClose={() => setOpenModal(false)}>
        <Box
          sx={{
            padding: 3,
            backgroundColor: 'white',
            borderRadius: 2,
            boxShadow: 3,
            width: '400px',
            margin: 'auto',
            marginTop: '20vh',
          }}
        >
          <Typography variant="h6" sx={{ mb: 2 }}>
            Enter Verification Code
          </Typography>
          <TextField
            fullWidth
            label="Verification Code"
            type="text"
            value={verificationCode}
            onChange={(event) => setVerificationCode(event.target.value)}
            sx={{ mb: 2 }}
          />
          <Button fullWidth onClick={handleVerifyCode} variant="contained" color="primary">
            Verify
          </Button>
          {error && (
            <Typography color="error" variant="body2" sx={{ mt: 2 }}>
              {error}
            </Typography>
          )}
        </Box>
      </Modal>
    </Box>
  );
};

export default Register;
