import React from 'react';
import { Box, Container, Grid, Typography, Button } from '@mui/material';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <Box sx={{ mt: 10, bgcolor: '#f5f5f5', py: 8 }}> {/* Added background color and padding */}
      <Container maxWidth="lg">
        <Grid container spacing={4} justifyContent="center"> {/* Centered content and increased spacing */}
          <Grid item xs={12} sm={8} md={6}> {/* Adjusted grid item sizes for better responsiveness */}
            <Box sx={{ p: 4, bgcolor: 'white', borderRadius: 2, boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)' }}> {/* Added background color, box shadow, and padding */}
              <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', color: '#333' }}> {/* Improved typography */}
                Bienvenue au Tribunal de Commerce de Casablanca
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph sx={{ lineHeight: 1.6 }}> {/* Improved typography */}
                Application moderne et réactive pour la gestion du Tribunal de Commerce de Casablanca.  Facilitant l'accès aux services et informations.
              </Typography>
              <Box sx={{ display: 'flex', justifyContent: 'center' }}> {/* Centered button */}
                <Link to="/login" style={{ textDecoration: 'none' }}>
                  <Button variant="contained" color="primary" sx={{ 
                    bgcolor: '#007bff', // Modern blue color
                    '&:hover': { bgcolor: '#0056b3' }, // Darker blue on hover
                    color: 'white', 
                    padding: '12px 24px', 
                    borderRadius: '8px', // Rounded corners
                    fontSize: '16px' // Larger font size
                  }}>
                    Se Connecter
                  </Button>
                </Link>
              </Box>
            </Box>
          </Grid>

          {/* Feature and Benefit sections - now in a carousel or expandable sections would be even better */}
          <Grid item xs={12} sm={8} md={6}>
            <Box sx={{ p: 4, bgcolor: 'white', borderRadius: 2, boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)' }}>
              <Typography variant="h5" component="h2" gutterBottom sx={{ fontWeight: 'bold', color: '#333' }}>
                Fonctionnalités
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph>
                Gestion des utilisateurs
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph>
                Gestion des réservations
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph>
                Gestion des départements
              </Typography>
              {/* Add more features as needed */}
            </Box>
          </Grid>

           <Grid item xs={12} sm={8} md={6}>
            <Box sx={{ p: 4, bgcolor: 'white', borderRadius: 2, boxShadow: '0px 2px 4px rgba(0, 0, 0, 0.1)' }}>
              <Typography variant="h5" component="h2" gutterBottom sx={{ fontWeight: 'bold', color: '#333' }}>
                Avantages
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph>
                Facile à utiliser
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph>
                Conception réactive
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph>
                Sécurisé
              </Typography>
              {/* Add more benefits as needed */}
            </Box>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Home;
