import React, { useState } from "react";
import {
  Button,
  TextField,
  Container,
  Typography,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
} from "@mui/material";
import axios from "axios";

function ReservationForm() {
  const [space, setSpace] = useState("");
  const [resource, setResource] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  const handleReservation = async () => {
    const reservationData = { space, resource, date, time };
    try {
      await axios.post(
        "http://127.0.0.1:5000/api/reservations",
        reservationData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );

      alert("Reservation made successfully!");
    } catch (error) {
      // Log detailed error information for debugging
      console.error("Error making reservation:", error);
      // Optionally, log additional properties:
      if (error.response) {
        // The request was made and the server responded with a status code
        console.error("Response data:", error.response.data);
        console.error("Response status:", error.response.status);
        console.error("Response headers:", error.response.headers);
      } else if (error.request) {
        // The request was made but no response was received
        console.error("Request made but no response received:", error.request);
      } else {
        // Something else happened in setting up the request
        console.error("Error setting up the request:", error.message);
      }
      alert("Error making reservation. Check console for details.");
    }
  };

  return (
    <Container>
      <Typography variant="h4" gutterBottom>
        Reserve a Space
      </Typography>
      <FormControl fullWidth>
        <InputLabel>Space</InputLabel>
        <Select value={space} onChange={(e) => setSpace(e.target.value)}>
          <MenuItem value="courtroom">Courtroom</MenuItem>
          <MenuItem value="meeting-room">Meeting Room</MenuItem>
        </Select>
      </FormControl>
      <TextField
        label="Resource"
        fullWidth
        value={resource}
        onChange={(e) => setResource(e.target.value)}
        margin="normal"
      />
      <TextField
        label="Date"
        type="date"
        fullWidth
        value={date}
        onChange={(e) => setDate(e.target.value)}
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
      />
      <TextField
        label="Time"
        type="time"
        fullWidth
        value={time}
        onChange={(e) => setTime(e.target.value)}
        margin="normal"
        InputLabelProps={{
          shrink: true,
        }}
      />
      <Button variant="contained" onClick={handleReservation}>
        Make Reservation
      </Button>
    </Container>
  );
}

export default ReservationForm;
