import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  styled,
  Box,
  Container,
  Chip,
  Divider,
  MenuItem,
  Select,
  InputLabel,
  FormControl,
  Button,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  CircularProgress
} from '@mui/material';
import axios from '../features/axios';
import dayjs from 'dayjs';
import 'dayjs/locale/fr';
dayjs.locale('fr');

// Styled components
const StyledCard = styled(Card)(({ theme }) => ({
  borderRadius: '16px',
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
  transition: 'transform 0.2s',
  '&:hover': {
    transform: 'scale(1.02)',
  },
}));

const ChefDashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [departmentName, setDepartmentName] = useState(null);
  const [loadingReservations, setLoadingReservations] = useState(true);
  const [loadingEmployees, setLoadingEmployees] = useState(true);
  const [loadingDepartment, setLoadingDepartment] = useState(true);
  const [errorReservations, setErrorReservations] = useState(null);
  const [errorEmployees, setErrorEmployees] = useState(null);
  const [errorDepartment, setErrorDepartment] = useState(null);
  
  // States for modal form
  const [openModal, setOpenModal] = useState(false);
  const [newEmployee, setNewEmployee] = useState({
    name: '',
    telephone: '',
    email: '',
    age: '',
    department: '',
    role: 'employee',
  });
  const [creatingEmployee, setCreatingEmployee] = useState(false);

  const decodeText = (text) => decodeURIComponent(escape(text));

  // Fetch department name
  useEffect(() => {
    const fetchDepartmentName = async () => {
      try {
        if (user && user.numdep) {
          const response = await axios.get('/departments');
          const departments = response.data;
          const foundDepartment = departments.find(dept => dept.id === user.numdep);
          if (foundDepartment) {
            setDepartmentName(decodeText(foundDepartment.nomdepart)); 
          } else {
            setErrorDepartment('Département non trouvé.');
          }
        } else {
          setErrorDepartment("Information de l'utilisateur manquante.");
        }
      } catch (error) {
        console.error("Erreur lors de la récupération du département:", error);
        setErrorDepartment("Erreur lors de la récupération du département.");
      } finally {
        setLoadingDepartment(false);
      }
    };

    fetchDepartmentName();
  }, [user]);

  // Fetch reservations
  useEffect(() => {
    if (departmentName) {
      const fetchReservations = async () => {
        try {
          const response = await axios.get('/chef/reservations', {
            params: { department: departmentName },
          });
          setReservations(response.data);
        } catch (error) {
          console.error("Erreur lors de la récupération des réservations:", error);
          setErrorReservations("Erreur lors de la récupération des réservations.");
        } finally {
          setLoadingReservations(false);
        }
      };
      fetchReservations();
    }
  }, [departmentName]);

  // Fetch employees
  useEffect(() => {
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('/chef/employees');
        setEmployees(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération des employés:", error);
        setErrorEmployees("Erreur lors de la récupération des employés.");
      } finally {
        setLoadingEmployees(false);
      }
    };

    fetchEmployees();
  }, []);

  // Handle adding a new employee
  const handleAddEmployee = async () => {
    setCreatingEmployee(true);
    try {
      const response = await axios.post('/chef/employees', newEmployee);
      setEmployees((prev) => [...prev, response.data]);
      setNewEmployee({
        name: '',
        telephone: '',
        email: '',
        age: '',
        department: '',
        role: 'employee',
      });
      setOpenModal(false);
    } catch (error) {
      console.error('Erreur lors de la création de l\'employé:', error);
    } finally {
      setCreatingEmployee(false);
    }
  };

  // Handle input change in the modal
  const handleChange = (e) => {
    setNewEmployee({
      ...newEmployee,
      [e.target.name]: e.target.value,
    });
  };

  if (loadingReservations || loadingEmployees) {
    return <CircularProgress />;
  }

  if (errorReservations || errorEmployees) {
    return (
      <Typography variant="h6" color="error">
        {errorReservations || errorEmployees}
      </Typography>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center' }}>
        Bonjour {user.name}, Chef du département {departmentName}
      </Typography>

      <Typography variant="h5" component="h2" gutterBottom sx={{ mt: 3 }}>
        Réservations
      </Typography>
      <Divider sx={{ mb: 2 }} />

      <Button
        variant="contained"
        color="primary"
        onClick={() => setOpenModal(true)}
        sx={{ mb: 3 }}
      >
        Créer un nouvel employé
      </Button>

      <Grid container spacing={3}>
        {employees.map((employee) => (
          <Grid item key={employee.id} xs={12} sm={6} md={4} lg={3}>
            <StyledCard>
              <CardContent>
                <Typography variant="h6" component="h3" gutterBottom sx={{ fontWeight: 'bold' }}>
                  {employee.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Téléphone: {employee.telephone}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Email: {employee.email}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Département: {employee.department}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Âge: {employee.age}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Rôle: {employee.role}
                </Typography>
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>

      {/* Modal to create employee */}
      <Dialog open={openModal} onClose={() => setOpenModal(false)}>
        <DialogTitle>Créer un nouvel employé</DialogTitle>
        <DialogContent>
          <TextField
            label="Nom"
            name="name"
            fullWidth
            value={newEmployee.name}
            onChange={handleChange}
            sx={{ mb: 2 }}
          />
          <TextField
            label="Téléphone"
            name="telephone"
            fullWidth
            value={newEmployee.telephone}
            onChange={handleChange}
            sx={{ mb: 2 }}
          />
          <TextField
            label="Email"
            name="email"
            fullWidth
            value={newEmployee.email}
            onChange={handleChange}
            sx={{ mb: 2 }}
          />
          <TextField
            label="Âge"
            name="age"
            fullWidth
            value={newEmployee.age}
            onChange={handleChange}
            sx={{ mb: 2 }}
          />
          <TextField
            label="Département"
            name="department"
            fullWidth
            value={newEmployee.department}
            onChange={handleChange}
            sx={{ mb: 2 }}
          />
          <TextField
            label="Rôle"
            name="role"
            fullWidth
            value={newEmployee.role}
            onChange={handleChange}
            sx={{ mb: 2 }}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenModal(false)} color="primary">
            Annuler
          </Button>
          <Button onClick={handleAddEmployee} color="primary" disabled={creatingEmployee}>
            {creatingEmployee ? 'Création en cours...' : 'Créer'}
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default ChefDashboard;
