import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import dayjs from 'dayjs';
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "@/components/ui/card";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogFooter,
} from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Badge } from "@/components/ui/badge";

const Dashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [departments, setDepartments] = useState([]);
  const [showAlert, setShowAlert] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('info');
  
  const [newReservation, setNewReservation] = useState({
    description: '',
    date: dayjs().format('YYYY-MM-DD'),
    time: '09:00',
    department: '',
    phone: '',
  });

  useEffect(() => {
    const fetchReservations = async () => {
      setLoading(true);
      try {
        if (user?.name) {
          const response = await fetch('/api/reservations/user?name=' + user.name);
          const data = await response.json();
          setReservations(data);
        } else {
          setError('User not logged in or name not available.');
        }
      } catch (err) {
        setError('Error fetching reservations. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchReservations();
  }, [user]);

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await fetch('/api/departments');
        const data = await response.json();
        setDepartments(data.map(dept => ({
          ...dept,
          nomdepart: decodeURIComponent(escape(dept.nomdepart))
        })));
      } catch (error) {
        console.error('Error fetching departments:', error);
      }
    };

    fetchDepartments();
  }, []);

  const handleCreateReservation = async () => {
    try {
      const response = await fetch('/api/reservations', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          ...newReservation,
          citizen: user.name,
          status: 'En attente',
        }),
      });

      if (!response.ok) throw new Error('Failed to create reservation');

      const data = await response.json();
      setReservations([...reservations, data]);
      setOpenModal(false);
      setAlertMessage('Reservation created successfully!');
      setAlertType('success');
      setShowAlert(true);
    } catch (error) {
      setError(error.message);
      setAlertMessage(error.message);
      setAlertType('error');
      setShowAlert(true);
    }
  };

  if (loading) {
    return <div className="text-xl text-center mt-8">Loading reservations...</div>;
  }

  if (error) {
    return <div className="text-xl text-red-500 text-center mt-8">{error}</div>;
  }

  return (
    <div className="container mx-auto px-4 mt-8">
      <h1 className="text-3xl font-bold text-center mb-8">Dashboard</h1>

      {(!reservations || reservations.length === 0) ? (
        <div className="text-center p-8 bg-white rounded-lg shadow-md">
          <h2 className="text-xl mb-4">No reservations found.</h2>
          <Button onClick={() => setOpenModal(true)}>
            Create Reservation
          </Button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {reservations.map((reservation) => (
            <Card key={reservation.id} className="flex flex-col h-full">
              <CardHeader>
                <CardTitle>Reservation Details</CardTitle>
              </CardHeader>
              <CardContent className="flex-grow">
                <div className="space-y-4">
                  <div>
                    <p className="text-sm text-gray-500">Description:</p>
                    <p>{reservation.description}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Date:</p>
                    <p>{reservation.date}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Time:</p>
                    <p>{reservation.time}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Department:</p>
                    <p>{reservation.department}</p>
                  </div>
                  <div>
                    <p className="text-sm text-gray-500">Phone:</p>
                    <p>{reservation.phone}</p>
                  </div>
                  <Badge 
                    variant={reservation.status === 'En attente' ? 'warning' : 'success'}
                  >
                    {reservation.status}
                  </Badge>
                </div>
                <div className="flex justify-end space-x-2 mt-4">
                  <Button variant="outline">Edit</Button>
                  <Button variant="destructive">Delete</Button>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}

      <Dialog open={openModal} onOpenChange={setOpenModal}>
        <DialogHeader>
          <DialogTitle>Create New Reservation</DialogTitle>
        </DialogHeader>
        <DialogContent>
          <div className="space-y-4">
            <Input
              placeholder="Description"
              value={newReservation.description}
              onChange={(e) => setNewReservation({
                ...newReservation,
                description: e.target.value
              })}
            />
            <Input
              type="date"
              value={newReservation.date}
              onChange={(e) => setNewReservation({
                ...newReservation,
                date: e.target.value
              })}
            />
            <Input
              type="time"
              value={newReservation.time}
              onChange={(e) => setNewReservation({
                ...newReservation,
                time: e.target.value
              })}
            />
            <Select
              value={newReservation.department}
              onValueChange={(value) => setNewReservation({
                ...newReservation,
                department: value
              })}
            >
              <SelectTrigger>
                <SelectValue placeholder="Select department" />
              </SelectTrigger>
              <SelectContent>
                {departments.map((department) => (
                  <SelectItem key={department.id} value={department.nomdepart}>
                    {department.nomdepart}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <Input
              placeholder="Phone number"
              value={newReservation.phone}
              onChange={(e) => setNewReservation({
                ...newReservation,
                phone: e.target.value
              })}
            />
          </div>
        </DialogContent>
        <DialogFooter>
          <Button variant="outline" onClick={() => setOpenModal(false)}>
            Cancel
          </Button>
          <Button onClick={handleCreateReservation}>
            Create
          </Button>
        </DialogFooter>
      </Dialog>

      {showAlert && (
        <Alert
          className="fixed bottom-4 right-4"
          variant={alertType === 'error' ? 'destructive' : 'default'}
        >
          <AlertDescription>
            {alertMessage}
          </AlertDescription>
        </Alert>
      )}
    </div>
  );
};

export default Dashboard;
