import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  Container,
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Snackbar,
  Alert,
  Paper,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  IconButton
} from '@mui/material';
import { CalendarDays, Clock, Phone, Building2, User, FileText } from 'lucide-react';
import axios from '../features/axios';
import { useSelector } from 'react-redux';
import dayjs from 'dayjs';
import 'dayjs/locale/fr';
dayjs.locale('fr');

const Dashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [newReservation, setNewReservation] = useState({
    description: '',
    date: dayjs().format('YYYY-MM-DD'),
    time: '09:00',
    department: '',
    phone: '',
  });
  const [departments, setDepartments] = useState([]);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('info');

  const statusColors = {
    'En attente': 'bg-amber-500',
    'Confirmé': 'bg-emerald-500',
    'Annulé': 'bg-red-500',
  };

  useEffect(() => {
    const fetchReservations = async () => {
      setLoading(true);
      setError(null);

      try {
        if (user && user.name) {
          const response = await axios.get('/reservations/user', {
            params: { name: user.name },
          });
          setReservations(response.data);
        } else {
          setError('Utilisateur non connecté ou nom indisponible.');
        }
      } catch (err) {
        console.error('Erreur lors de la récupération des réservations:', err);
        setError('Erreur lors de la récupération des réservations. Veuillez réessayer plus tard.');
      } finally {
        setLoading(false);
      }
    };

    fetchReservations();
  }, [user]);

  useEffect(() => {
    const fetchDepartments = async () => {
      try {
        const response = await axios.get('/departments');
        const decodedDepartments = response.data.map((dept) => ({
          ...dept,
          nomdepart: decodeURIComponent(escape(dept.nomdepart)),
        }));
        setDepartments(decodedDepartments);
        console.log('Départements:', decodedDepartments);
      } catch (error) {
        console.error('Erreur lors de la récupération des départements:', error);
      }
    };

    fetchDepartments();
  }, []);

  const handleOpenModal = () => {
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
    setNewReservation({
      description: '',
      date: dayjs().format('YYYY-MM-DD'),
      time: '09:00',
      department: '',
    });
    setError(null);
  };

  const handleInputChange = (event) => {
    setNewReservation({ ...newReservation, [event.target.name]: event.target.value });
  };

  const handleCreateReservation = async () => {
    setError(null);
    try {
      const reservationData = {
        ...newReservation,
        citizen: user.name,
        status: 'En attente',
      };

      const response = await axios.post('/reservations', reservationData);

      setReservations([...reservations, response.data]);
      handleCloseModal();
      setSnackbarMessage('Réservation créée avec succès!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
    } catch (error) {
      console.error('Erreur lors de la création de la réservation:', error);
      setError(error.response?.data?.error || 'Erreur lors de la création de la réservation. Veuillez réessayer.');
      setSnackbarMessage(
        error.response?.data?.error || 'Erreur lors de la création de la réservation. Veuillez réessayer.'
      );
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleSnackbarClose = (event, reason) => {
    if (reason === 'clickaway') {
      return;
    }
    setSnackbarOpen(false);
  };

  if (loading) {
    return <Typography variant="h6">Chargement des réservations...</Typography>;
  }

  if (error) {
    return (
      <Typography variant="h6" color="error">
        {error}
      </Typography>
    );
  }

  return (
    <Box sx={{ mt: 4 }}>
      <Container maxWidth="lg">
        <Typography
          variant="h4"
          component="h1"
          gutterBottom
          sx={{ fontWeight: 'bold', mb: 4, textAlign: 'center' }}
        >
          Tableau de Bord des Réservations
        </Typography>

        <Typography variant="h6" sx={{ mb: 4, textAlign: 'center' }}>
          Cette page vous permet de consulter et gérer vos réservations. Vous pouvez également créer de nouvelles réservations en cliquant sur le bouton ci-dessous.
        </Typography>

        {/* Conditional rendering based on reservations */}
        {!reservations || reservations.length === 0 ? (
          <Paper elevation={3} sx={{ padding: 4, textAlign: 'center' }}>
            <Typography variant="h6" sx={{ mb: 2 }}>
              Aucune réservation trouvée.
            </Typography>
            <Button variant="contained" color="primary" onClick={handleOpenModal}>
              Créer une Réservation
            </Button>
          </Paper>
        ) : (
          <Grid container spacing={3} sx={{ width: '100%', px: 0 }}>
            {reservations.map((reservation) => (
              <Grid item key={reservation.id} xs={96} sm={48} md={32} lg={24}>
                <Card elevation={3} sx={{ borderRadius: '12px', backgroundColor: '#333', color: '#FFF' }}>
                  <CardContent sx={{ padding: '30px 20px' }}>
                    <Typography variant="h6" sx={{ fontWeight: 'bold', fontSize: '24px', marginBottom: '25px' }}>
                      Détails de la Réservation
                    </Typography>
                    <Box sx={{ display: 'flex', flexDirection: 'row', flexWrap: 'wrap' }}>
                      <Box sx={{ width: '50%', mb: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          Description:
                        </Typography>
                        <Typography variant="body1">{reservation.description}</Typography>
                      </Box>
                      <Box sx={{ width: '50%', mb: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          Date:
                        </Typography>
                        <Typography variant="body1">{reservation.date}</Typography>
                      </Box>
                      <Box sx={{ width: '50%', mb: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          Heure:
                        </Typography>
                        <Typography variant="body1">{reservation.time}</Typography>
                      </Box>
                      <Box sx={{ width: '50%', mb: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          Département:
                        </Typography>
                        <Typography variant="body1">{reservation.department}</Typography>
                      </Box>
                      <Box sx={{ width: '50%', mb: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          Citoyen:
                        </Typography>
                        <Typography variant="body1">{reservation.citizen}</Typography>
                      </Box>
                      <Box sx={{ width: '50%', mb: 1 }}>
                        <Typography variant="body2" color="text.secondary">
                          Téléphone:
                        </Typography>
                        <Typography variant="body1">{reservation.phone}</Typography>
                      </Box>
                    </Box>
                    <Chip
                      label={`Statut: ${reservation.status}`}
                      color={reservation.status === 'En attente' ? 'warning' : 'success'}
                      size="small"
                      sx={{ mt: 2 }}
                    />
                  </CardContent>
                </Card>
              </Grid>
            ))}
          </Grid>
        )}
      </Container>

      {/* Reservation Modal */}
      <Dialog open={openModal} onClose={handleCloseModal}>
        <DialogTitle>Créer une Nouvelle Réservation</DialogTitle>
        <DialogContent>
          <TextField
            fullWidth
            label="Description"
            name="description"
            value={newReservation.description}
            onChange={handleInputChange}
            margin="normal"
          />
          <TextField
            fullWidth
            label="Date"
            type="date"
            name="date"
            value={newReservation.date}
            onChange={handleInputChange}
            margin="normal"
            InputLabelProps={{ shrink: true }}
          />
          <TextField
            fullWidth
            label="Heure"
            type="time"
            name="time"
            value={newReservation.time}
            onChange={handleInputChange}
            margin="normal"
            InputLabelProps={{ shrink: true }}
          />
          <FormControl fullWidth margin="normal">
            <InputLabel>Département</InputLabel>
            <Select
              value={newReservation.department}
              onChange={handleInputChange}
              label="Département"
              name="department"
            >
              {departments.map((dept) => (
                <MenuItem key={dept.id} value={dept.id}>
                  {dept.nomdepart}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
          <TextField
            fullWidth
            label="Téléphone"
            name="phone"
            value={newReservation.phone}
            onChange={handleInputChange}
            margin="normal"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseModal} color="secondary">
            Annuler
          </Button>
          <Button onClick={handleCreateReservation} color="primary">
            Créer
          </Button>
        </DialogActions>
      </Dialog>

      {/* Snackbar for feedback */}
      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={handleSnackbarClose}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert onClose={handleSnackbarClose} severity={snackbarSeverity} sx={{ width: '100%' }}>
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default Dashboard;
