import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  Button,
  TextField,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
} from '@mui/material';
import axios from '../features/axios'; // Assuming you have an axios instance

const AdminDashboard = () => {
  const [reservations, setReservations] = useState();
  const [employees, setEmployees] = useState();
  const [users, setUsers] = useState();
  const [departments, setDepartments] = useState();

  // State for new user registration
  const [newUser, setNewUser] = useState({
    name: '',
    email: '',
    password: '',
    role: 'chef', // Default role is 'chef'
    numdep: '',
  });

  // State for dialogs
  const [openReservationDialog, setOpenReservationDialog] = useState(false);
  const [openEmployeeDialog, setOpenEmployeeDialog] = useState(false);
  const [openUserDialog, setOpenUserDialog] = useState(false);

  // State for editing existing entities
  const [editingReservation, setEditingReservation] = useState(null);
  const [editingEmployee, setEditingEmployee] = useState(null);
  const [editingUser, setEditingUser] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const reservationsResponse = await axios.get('/reservations');
        setReservations(reservationsResponse.data);

        const employeesResponse = await axios.get('/chef/employees');
        setEmployees(employeesResponse.data);

        const usersResponse = await axios.get('/users');
        setUsers(usersResponse.data);

        const departmentsResponse = await axios.get('/departments');
        setDepartments(departmentsResponse.data);
      } catch (error) {
        console.error(error);
      }
    };

    fetchData();
  });

  // --- User Management ---
  const handleCreateUser = async () => {
    try {
      await axios.post('/register', newUser);
      // Update user list after successful creation
      const usersResponse = await axios.get('/users');
      setUsers(usersResponse.data);
      setNewUser({
        name: '',
        email: '',
        password: '',
        role: 'chef',
        numdep: '',
      });
      setOpenUserDialog(false);
    } catch (error) {
      console.error('Error creating user:', error);
      // Handle error, e.g., show an error message to the user
    }
  };

  const handleUpdateUser = async () => {
    try {
      await axios.put(`/users/${editingUser.id}`, editingUser);
      const usersResponse = await axios.get('/users');
      setUsers(usersResponse.data);
      setEditingUser(null);
      setOpenUserDialog(false);
    } catch (error) {
      console.error('Error updating user:', error);
    }
  };

  const handleDeleteUser = async (userId) => {
    try {
      await axios.delete(`/users/${userId}`);
      setUsers(users.filter((user) => user.id !== userId));
    } catch (error) {
      console.error('Error deleting user:', error);
    }
  };

  // --- Reservation Management ---
  const handleCreateReservation = async () => {
    try {
      await axios.post('/reservations', editingReservation);
      const reservationsResponse = await axios.get('//reservations');
      setReservations(reservationsResponse.data);
      setEditingReservation(null);
      setOpenReservationDialog(false);
    } catch (error) {
      console.error('Error creating reservation:', error);
    }
  };

  const handleUpdateReservation = async () => {
    try {
      await axios.put(`/reservations/${editingReservation.id}`, editingReservation);
      const reservationsResponse = await axios.get('/reservations');
      setReservations(reservationsResponse.data);
      setEditingReservation(null);
      setOpenReservationDialog(false);
    } catch (error) {
      console.error('Error updating reservation:', error);
    }
  };

  const handleDeleteReservation = async (reservationId) => {
    try {
      await axios.delete(`/reservations/${reservationId}`);
      setReservations(reservations.filter((res) => res.id !== reservationId));
    } catch (error) {
      console.error('Error deleting reservation:', error);
    }
  };

  // --- Employee Management ---
  const handleCreateEmployee = async () => {
    try {
      await axios.post('/chef/employees', editingEmployee);
      const employeesResponse = await axios.get('/chef/employees');
      setEmployees(employeesResponse.data);
      setEditingEmployee(null);
      setOpenEmployeeDialog(false);
    } catch (error) {
      console.error('Error creating employee:', error);
    }
  };

  const handleUpdateEmployee = async () => {
    try {
      await axios.put(`/chef/employees/${editingEmployee.id}`, editingEmployee);
      const employeesResponse = await axios.get('/chef/employees');
      setEmployees(employeesResponse.data);
      setEditingEmployee(null);
      setOpenEmployeeDialog(false);
    } catch (error) {
      console.error('Error updating employee:', error);
    }
  };

  const handleDeleteEmployee = async (employeeId) => {
    try {
      await axios.delete(`/chef/employees/${employeeId}`);
      setEmployees(employees.filter((emp) => emp.id !== employeeId));
    } catch (error) {
      console.error('Error deleting employee:', error);
    }
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>

      {/* User Management */}
      <h2>Users</h2>
      <Button variant="contained" color="primary" onClick={() => setOpenUserDialog(true)}>
        New User
      </Button>
      <Grid container spacing={2}>
        {users && users.length > 0 ? ( // Check if users is defined and not empty
          users.map((user) => (
            <Grid item key={user.id} xs={12} sm={6} md={4} lg={3}>
              <Card>
                <CardContent>
                  <Typography variant="h5" component="h2">
                    {user.name}
                  </Typography>
                  <Typography variant="body2" color="textSecondary" component="p">
                    {user.email}
                  </Typography>
                  <Typography variant="body2" color="textSecondary" component="p">
                    Role: {user.role}
                  </Typography>
                  <Button
                    variant="outlined"
                    color="primary"
                    onClick={() => {
                      setEditingUser({ ...user });
                      setOpenUserDialog(true);
                    }}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="outlined"
                    color="error"
                    onClick={() => handleDeleteUser(user.id)}
                  >
                    Delete
                  </Button>
                </CardContent>
              </Card>
            </Grid>
          ))
        ) : (
          <Typography>Loading users...</Typography> // Or a loading spinner
        )}
      </Grid>

      {/* Reservation Management */}
      <h2>Reservations</h2>
      <Button variant="contained" color="primary" onClick={() => setOpenReservationDialog(true)}>
        New Reservation
      </Button>
      <Grid container spacing={2}>
      {reservations && reservations.length > 0 ? (
        reservations.map((reservation) => (
          <Grid item key={reservation.id} xs={12} sm={6} md={4} lg={3}>
            <Card>
              <CardContent>
                <Typography variant="h5" component="h2">
                  {reservation.description}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {reservation.date} {reservation.time}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {reservation.department}
                </Typography>
                <Button
                  variant="outlined"
                  color="primary"
                  onClick={() => {
                    setEditingReservation({ ...reservation });
                    setOpenReservationDialog(true);
                  }}
                >
                  Edit
                </Button>
                <Button
                  variant="outlined"
                  color="error"
                  onClick={() => handleDeleteReservation(reservation.id)}
                >
                  Delete
                </Button>
              </CardContent>
            </Card>
          </Grid>
        )) ) : (
          <Typography>Loading reservations...</Typography>
      )}
      </Grid>

      {/* Employee Management */}
      <h2>Employees</h2>
      <Button variant="contained" color="primary" onClick={() => setOpenEmployeeDialog(true)}>
        New Employee
      </Button>
      <Grid container spacing={2}>
      {employees && employees.length > 0 ? (
        employees.map((employee) => (
          <Grid item key={employee.id} xs={12} sm={6} md={4} lg={3}>
            <Card>
              <CardContent>
                <Typography variant="h5" component="h2">
                  {employee.name}
                </Typography>
                <Typography variant="body2" color="textSecondary" component="p">
                  {employee.email}
                </Typography>
                <Button
                  variant="outlined"
                  color="primary"
                  onClick={() => {
                    setEditingEmployee({ ...employee });
                    setOpenEmployeeDialog(true);
                  }}
                >
                  Edit
                </Button>
                <Button
                  variant="outlined"
                  color="error"
                  onClick={() => handleDeleteEmployee(employee.id)}
                >
                  Delete
                </Button>
              </CardContent>
            </Card>
          </Grid>
        ))) : (
          <Typography>Loading employees...</Typography>
      )}
      </Grid>

      {/* Dialogs */}
      {/* User Dialog */}
      <Dialog open={openUserDialog} onClose={() => setOpenUserDialog(false)}>
        <DialogTitle>{editingUser ? 'Edit User' : 'New User'}</DialogTitle>
        <DialogContent>
          <TextField
            label="Name"
            fullWidth
            value={editingUser ? editingUser.name : newUser.name}
            onChange={(e) =>
              editingUser
                ? setEditingUser({ ...editingUser, name: e.target.value })
                : setNewUser({ ...newUser, name: e.target.value })
            }
          />
          <TextField
            label="Email"
            fullWidth
            value={editingUser ? editingUser.email : newUser.email}
            onChange={(e) =>
              editingUser
                ? setEditingUser({ ...editingUser, email: e.target.value })
                : setNewUser({ ...newUser, email: e.target.value })
            }
          />
          <TextField
            label="Password"
            fullWidth
            type="password"
            value={editingUser ? editingUser.password : newUser.password}
            onChange={(e) =>
              editingUser
                ? setEditingUser({ ...editingUser, password: e.target.value })
                : setNewUser({ ...newUser, password: e.target.value })
            }
          />
          <FormControl fullWidth>
            <InputLabel>Role</InputLabel>
            <Select
              value={editingUser ? editingUser.role : newUser.role}
              onChange={(e) =>
                editingUser
                  ? setEditingUser({ ...editingUser, role: e.target.value })
                  : setNewUser({ ...newUser, role: e.target.value })
              }
            >
              <MenuItem value="chef">Chef</MenuItem>
              <MenuItem value="employee">Employee</MenuItem>
              {/* Add other roles as needed */}
            </Select>
          </FormControl>
          <FormControl fullWidth>
            <InputLabel>Department</InputLabel>
            <Select
              value={editingUser ? editingUser.numdep : newUser.numdep}
              onChange={(e) =>
                editingUser
                  ? setEditingUser({ ...editingUser, numdep: e.target.value })
                  : setNewUser({ ...newUser, numdep: e.target.value })
              }
            >
              {departments.map((department) => (
                <MenuItem key={department.id} value={department.id}>
                  {department.nomdepart}
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenUserDialog(false)}>Cancel</Button>
          {editingUser ? (
            <Button onClick={handleUpdateUser}>Update</Button>
          ) : (
            <Button onClick={handleCreateUser}>Create</Button>
          )}
        </DialogActions>
      </Dialog>

      {/* Reservation Dialog */}
      <Dialog open={openReservationDialog} onClose={() => setOpenReservationDialog(false)}>
        <DialogTitle>{editingReservation ? 'Edit Reservation' : 'New Reservation'}</DialogTitle>
        <DialogContent>
          {/* Add fields for reservation details (description, date, time, department, etc.) */}
          {/* Similar to User Dialog, use editingReservation or a new reservation object */}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenReservationDialog(false)}>Cancel</Button>
          {editingReservation ? (
            <Button onClick={handleUpdateReservation}>Update</Button>
          ) : (
            <Button onClick={handleCreateReservation}>Create</Button>
          )}
        </DialogActions>
      </Dialog>

      {/* Employee Dialog */}
      <Dialog open={openEmployeeDialog} onClose={() => setOpenEmployeeDialog(false)}>
        <DialogTitle>{editingEmployee ? 'Edit Employee' : 'New Employee'}</DialogTitle>
        <DialogContent>
          {/* Add fields for employee details (name, email, etc.) */}
          {/* Similar to User Dialog, use editingEmployee or a new employee object */}
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenEmployeeDialog(false)}>Cancel</Button>
          {editingEmployee ? (
            <Button onClick={handleUpdateEmployee}>Update</Button>
          ) : (
            <Button onClick={handleCreateEmployee}>Create</Button>
          )}
        </DialogActions>
      </Dialog>
    </div>
  );
};

export default AdminDashboard;
