import React from 'react';
import { AppBar, Toolbar, Container, Typography, Button, Stack } from '@mui/material';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../actions';

const Navbar = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);

  const handleLogout = () => {
    dispatch(logout());
    window.location.href = '/'; // Or use react-router's history.push
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: '#232323', color: '#fff', py: 1, boxShadow: 2 }}>
      <Container maxWidth="lg">
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between' }}>
          <Typography variant="h5" component="h2" fontFamily="'Roboto', sans-serif" fontWeight="500">
            Tribunal de Commerce à Casablanca
          </Typography>

          <Stack direction="row" spacing={2} alignItems="center">
            {!user ? (
              <>
                <Button component={Link} to="/" sx={linkStyle}>Accueil</Button>
                <Button component={Link} to="/login" sx={linkStyle}>Connexion</Button>
                <Button component={Link} to="/register" sx={linkStyle}>Inscription</Button>
              </>
            ) : (
              <Button variant="outlined" sx={logoutButtonStyle} onClick={handleLogout}>Déconnexion</Button>
            )}

          </Stack>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

const linkStyle = {
  textDecoration: 'none',
  color: '#fff',
  fontSize: '1rem',
  textTransform: 'none',
  '&:hover': {
    textDecoration: 'underline',
  },
};

const logoutButtonStyle = {
  color: '#fff',
  borderColor: '#fff',
  textTransform: 'none',
  '&:hover': {
    borderColor: '#ddd',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
  },
};

const contactButtonStyle = {
  fontSize: '0.875rem',
  textTransform: 'none',
};

const aboutButtonStyle = {
  fontSize: '0.875rem',
  textTransform: 'none',
};

export default Navbar;
