import React, { useState, useEffect } from 'react';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  Box,
  Container,
  Button,
  TextField,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  Snackbar,
  Alert,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
} from '@mui/material';
import axios from '../features/axios';
import { useSelector } from 'react-redux';
import dayjs from 'dayjs';
import 'dayjs/locale/fr';
import { CalendarDays, Clock, Phone, Building2, User, FileText } from 'lucide-react';

dayjs.locale('fr');

const Dashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [departments, setDepartments] = useState([]);
  const [newReservation, setNewReservation] = useState({
    description: '',
    date: dayjs().format('YYYY-MM-DD'),
    time: '09:00',
    department: '',
    phone: '',
  });
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('info');

  const statusColors = {
    'En attente': 'bg-amber-500',
    'Confirmé': 'bg-emerald-500',
    'Annulé': 'bg-red-500',
  };

  const handleInputChange = (e) => {
    setNewReservation({ ...newReservation, [e.target.name]: e.target.value });
  };

  const fetchReservations = async () => {
    setLoading(true);
    setError(null);

    try {
      if (user && user.name) {
        const response = await axios.get('/reservations/user', {
          params: { name: user.name },
        });
        setReservations(response.data);
      } else {
        setError('Utilisateur non connecté ou nom indisponible.');
      }
    } catch (err) {
      console.error('Erreur lors de la récupération des réservations:', err);
      setError('Erreur lors de la récupération des réservations. Veuillez réessayer plus tard.');
    } finally {
      setLoading(false);
    }
  };

  const fetchDepartments = async () => {
    try {
      const response = await axios.get('/departments');
      setDepartments(response.data);
    } catch (error) {
      console.error('Erreur lors de la récupération des départements:', error);
    }
  };

  useEffect(() => {
    fetchReservations();
    fetchDepartments();
  }, [user]);

  if (loading) {
    return <Typography variant="h6">Chargement des réservations...</Typography>;
  }

  if (error) {
    return (
      <Typography variant="h6" color="error">
        {error}
      </Typography>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-b from-amber-50 to-orange-50 pb-12">
      {/* Decorative header */}
      <div className="h-4 bg-gradient-to-r from-emerald-600 via-emerald-500 to-emerald-600" />

      <Container maxWidth="lg">
        <Typography variant="h4" component="h1" gutterBottom className="text-center font-bold text-xl mt-12">
          Tableau de Bord des Réservations
        </Typography>
        <Typography variant="h6" className="mb-4 text-center">
          Cette page vous permet de consulter et gérer vos réservations.
        </Typography>

        {/* Reservation Display */}
        <Grid container spacing={3} className="justify-center">
          {reservations.map((reservation) => (
            <Grid item xs={12} sm={6} md={4} key={reservation.id} className="flex justify-center">
              <Card className="w-full bg-white shadow-lg rounded-lg p-4">
                <CardContent>
                  <Typography variant="h6" className="text-lg font-semibold">
                    Détails de la Réservation
                  </Typography>
                  <div className="space-y-2">
                    <div className="flex justify-between">
                      <Typography variant="body2" color="text.secondary">Description:</Typography>
                      <Typography variant="body1">{reservation.description}</Typography>
                    </div>
                    <div className="flex justify-between">
                      <Typography variant="body2" color="text.secondary">Date:</Typography>
                      <Typography variant="body1">{reservation.date}</Typography>
                    </div>
                    <div className="flex justify-between">
                      <Typography variant="body2" color="text.secondary">Heure:</Typography>
                      <Typography variant="body1">{reservation.time}</Typography>
                    </div>
                    <div className="flex justify-between">
                      <Typography variant="body2" color="text.secondary">Département:</Typography>
                      <Typography variant="body1">{reservation.department}</Typography>
                    </div>
                    <div className="flex justify-between">
                      <Typography variant="body2" color="text.secondary">Citoyen:</Typography>
                      <Typography variant="body1">{reservation.citizen}</Typography>
                    </div>
                    <div className="flex justify-between">
                      <Typography variant="body2" color="text.secondary">Téléphone:</Typography>
                      <Typography variant="body1">{reservation.phone}</Typography>
                    </div>
                    <Chip
                      label={`Statut: ${reservation.status}`}
                      className={`mt-2 ${statusColors[reservation.status]} text-white`}
                    />
                  </div>
                </CardContent>
              </Card>
            </Grid>
          ))}
        </Grid>

        {/* Modal */}
        <Dialog open={openModal} onClose={() => setOpenModal(false)}>
          <DialogTitle>Créer une Nouvelle Réservation</DialogTitle>
          <DialogContent>
            <TextField
              fullWidth
              label="Description"
              name="description"
              value={newReservation.description}
              onChange={handleInputChange}
              margin="normal"
            />
            <TextField
              fullWidth
              label="Date"
              type="date"
              name="date"
              value={newReservation.date}
              onChange={handleInputChange}
              margin="normal"
              InputLabelProps={{
                shrink: true,
              }}
            />
            <TextField
              fullWidth
              label="Heure"
              type="time"
              name="time"
              value={newReservation.time}
              onChange={handleInputChange}
              margin="normal"
              InputLabelProps={{
                shrink: true,
              }}
            />
            <FormControl fullWidth margin="normal">
              <InputLabel id="department-label">Département</InputLabel>
              <Select
                labelId="department-label"
                name="department"
                value={newReservation.department}
                onChange={handleInputChange}
              >
                {departments.map((department) => (
                  <MenuItem key={department.id} value={department.nomdepart}>
                    {department.nomdepart}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
            <TextField
              fullWidth
              label="Numéro de Téléphone"
              name="phone"
              value={newReservation.phone}
              onChange={handleInputChange}
              margin="normal"
            />
          </DialogContent>
          <DialogActions>
            <Button onClick={() => setOpenModal(false)} color="primary">Annuler</Button>
            <Button onClick={() => {}} color="primary">Créer</Button>
          </DialogActions>
        </Dialog>

        {/* Snackbar */}
        <Snackbar open={snackbarOpen} autoHideDuration={6000} onClose={() => setSnackbarOpen(false)}>
          <Alert onClose={() => setSnackbarOpen(false)} severity={snackbarSeverity}>
            {snackbarMessage}
          </Alert>
        </Snackbar>
      </Container>
    </div>
  );
};

export default Dashboard;
