import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { AppBar, Toolbar, Typography, Button, Container, Drawer, List, ListItem, ListItemText, IconButton, Box } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import LoginPage from "./components/LoginPage";
import ReservationForm from "./components/ReservationForm";
import ReservationList from "./components/ReservationList";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [drawerOpen, setDrawerOpen] = useState(false);

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  return (
    <Router>
      <div>
        {/* Navigation Bar */}
        <AppBar position="sticky">
          <Toolbar>
            {/* Menu Icon */}
            {isLoggedIn && (
              <IconButton edge="start" color="inherit" onClick={toggleDrawer} sx={{ marginRight: 2 }}>
                <MenuIcon />
              </IconButton>
            )}
            {/* Title */}
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              My Reservation App
            </Typography>

            {/* Login or User Info */}
            {isLoggedIn ? (
              <Button color="inherit" onClick={handleLogout}>
                Logout
              </Button>
            ) : (
              <Button color="inherit" component={Link} to="/">
                Login
              </Button>
            )}
          </Toolbar>
        </AppBar>

        {/* Sidebar Drawer */}
        {isLoggedIn && (
          <Drawer
            sx={{
              width: 250,
              position: "sticky",
              top: 78, // Ensures it starts below the AppBar and gives your desired padding
              left: 0,
              height: '100vh', // Full height of the viewport
              zIndex: 1200, // Ensure it's under the AppBar but above other content
            }}
            variant="persistent"
            anchor="left"
            open={drawerOpen}
          >
            <List>
              <ListItem button component={Link} to="/reservations" onClick={toggleDrawer}>
                <ListItemText primary="Reservations" />
              </ListItem>
              <ListItem button component={Link} to="/reserve" onClick={toggleDrawer}>
                <ListItemText primary="Reserve" />
              </ListItem>
              <ListItem button onClick={handleLogout}>
                <ListItemText primary="Logout" />
              </ListItem>
            </List>
          </Drawer>
        )}

        {/* Main Content Area */}
        <Box sx={{ marginLeft: drawerOpen ? '250px' : '0', transition: 'margin-left 0.3s ease', position: 'relative' }}>
          <Container>
            <Routes>
              <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
              <Route path="/reservations" element={isLoggedIn ? <ReservationList /> : <LoginPage onLogin={handleLogin} />} />
              <Route path="/reserve" element={isLoggedIn ? <ReservationForm /> : <LoginPage onLogin={handleLogin} />} />
            </Routes>
          </Container>
        </Box>
      </div>
    </Router>
  );
}

export default App;
