import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, Link } from "react-router-dom";
import { AppBar, Toolbar, Typography, Button, Container, Drawer, List, ListItem, ListItemText, IconButton } from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";
import LoginPage from "./components/LoginPage";
import ReservationForm from "./components/ReservationForm";
import ReservationList from "./components/ReservationList";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [username, setUsername] = useState("");

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  return (
    <Router>
      <div>
        {/* Navigation Bar */}
        <AppBar position="sticky">
          <Toolbar>
            <Typography variant="h6" sx={{ flexGrow: 1 }}>
              My Reservation App
            </Typography>
            {isLoggedIn ? (
              <>
                <IconButton edge="start" color="inherit" onClick={toggleDrawer}>
                  <MenuIcon />
                </IconButton>
                <Button color="inherit" onClick={handleLogout}>
                  Logout
                </Button>
              </>
            ) : (
              <Button color="inherit" component={Link} to="/">
                Login
              </Button>
            )}
          </Toolbar>
        </AppBar>

        {/* Sidebar Drawer */}
        <Drawer anchor="left" open={drawerOpen} onClose={toggleDrawer}>
          <List>
            <ListItem button component={Link} to="/reservations" onClick={toggleDrawer}>
              <ListItemText primary="Reservations" />
            </ListItem>
            <ListItem button component={Link} to="/reserve" onClick={toggleDrawer}>
              <ListItemText primary="Reserve" />
            </ListItem>
            <ListItem button onClick={handleLogout}>
              <ListItemText primary="Logout" />
            </ListItem>
          </List>
        </Drawer>

        {/* Routes */}
        <Container sx={{ marginTop: "20px" }}>
          <Routes>
            <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
            <Route path="/reservations" element={isLoggedIn ? <ReservationList /> : <LoginPage onLogin={handleLogin} />} />
            <Route path="/reserve" element={isLoggedIn ? <ReservationForm /> : <LoginPage onLogin={handleLogin} />} />
          </Routes>
        </Container>
      </div>
    </Router>
  );
}

export default App;
