import React from 'react';
import { Box, Container, Grid, Typography, Button } from '@mui/material';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../actions';

const Navbar = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);

  const handleLogout = () => {
    dispatch(logout());
    window.location.href = '/';
  };

  return (
    <Box sx={{ backgroundColor: '#111', color: '#fff', py: 1.5, boxShadow: 3 }}>
      <Container maxWidth="lg">
        <Grid container spacing={4} alignItems="center">
          {/* Logo and Title */}
          <Grid item xs={12} sm={6}>
            <Typography
              variant="h4"
              component="h2"
              fontFamily="'Roboto', sans-serif"
              fontWeight="700"
              sx={{
                textAlign: { xs: 'center', sm: 'left' },
                fontSize: { xs: '1.5rem', sm: '2rem' },
              }}
            >
              Tribunal de Commerce à Casablanca
            </Typography>
          </Grid>

          {/* Navigation Links */}
          <Grid item xs={12} sm={6}>
            <Grid container justifyContent="center" spacing={2}>
              {!user ? (
                <>
                  <Grid item>
                    <Link to="/" style={linkStyle}>Accueil</Link>
                  </Grid>
                  <Grid item>
                    <Link to="/login" style={linkStyle}>Connexion</Link>
                  </Grid>
                  <Grid item>
                    <Link to="/register" style={linkStyle}>Inscription</Link>
                  </Grid>
                </>
              ) : (
                <Grid item>
                  <Button
                    variant="outlined"
                    sx={{ color: '#fff', borderColor: '#fff', '&:hover': { borderColor: '#1db954' } }}
                    onClick={handleLogout}
                  >
                    Logout
                  </Button>
                </Grid>
              )}
            </Grid>
          </Grid>

          {/* Action Buttons */}
          <Grid item xs={12}>
            <Grid container justifyContent="space-between" spacing={2}>
              <Grid item>
                <Button
                  variant="contained"
                  color="success"
                  sx={{
                    fontSize: '0.875rem',
                    padding: '8px 16px',
                    '&:hover': { backgroundColor: '#1db954' },
                  }}
                >
                  Contactez-nous
                </Button>
              </Grid>
              <Grid item>
                <Button
                  variant="contained"
                  color="error"
                  sx={{
                    fontSize: '0.875rem',
                    padding: '8px 16px',
                    '&:hover': { backgroundColor: '#e10000' },
                  }}
                >
                  À propos de nous
                </Button>
              </Grid>
            </Grid>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

const linkStyle = {
  textDecoration: 'none',
  color: '#fff',
  fontSize: '1.125rem',
  fontWeight: '500',
  '&:hover': {
    textDecoration: 'underline',
    color: '#1db954', // Add hover effect with brand color
  },
};

export default Navbar;
