import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { Box, Container, List, ListItem, ListItemText } from "@mui/material";
import Navbar from "./components/Navbar";  
import LoginPage from "./components/LoginPage";
import ReservationForm from "./components/ReservationForm";
import ReservationList from "./components/ReservationList";
import "@fontsource/poppins";
import "@fontsource/roboto";

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState("");
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const handleLogin = (username) => {
    setIsLoggedIn(true);
    setUsername(username);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername("");
  };

  const toggleSidebar = () => {
    setSidebarOpen(!sidebarOpen);
  };

  return (
    <Router>
      <div>
        {/* Navbar Component */}
        <Navbar
          isLoggedIn={isLoggedIn}
          toggleSidebar={toggleSidebar}
          handleLogout={handleLogout}
          handleLogin={handleLogin}
        />

        {/* Sidebar Menu (only visible if logged in) */}
        {isLoggedIn && (
          <Box
            sx={{
              position: "fixed",
              top: 70,
              left: sidebarOpen ? 0 : -250,
              width: 250,
              height: "calc(100vh - 64px)",
              backgroundColor: "#f8f9fa",
              color: "#333",
              boxShadow: "2px 0 5px rgba(0,0,0,0.2)",
              borderTopRightRadius: "12px",
              borderBottomRightRadius: "12px",
              transition: "left 0.3s ease-in-out",
              zIndex: 1200,
              fontFamily: "Roboto, sans-serif",
            }}
          >
            <List>
              <ListItem
                button
                component={Link}
                to="/reservations"
                onClick={toggleSidebar}
                sx={{
                  "&:hover": {
                    backgroundColor: "#00d2ff",
                    color: "#fff",
                  },
                }}
              >
                <ListItemText primary="Réservations" />
              </ListItem>
              <ListItem
                button
                component={Link}
                to="/reserve"
                onClick={toggleSidebar}
                sx={{
                  "&:hover": {
                    backgroundColor: "#00d2ff",
                    color: "#fff",
                  },
                }}
              >
                <ListItemText primary="Réserver" />
              </ListItem>
            </List>
          </Box>
        )}

        {/* Main Content */}
        <Box
          sx={{
            marginTop: "64px",
            marginLeft: sidebarOpen ? "250px" : "0",
            transition: "margin-left 0.3s ease",
            padding: "20px",
            backgroundColor: "#f4f6f9",
            minHeight: "calc(100vh - 64px)",
            fontFamily: "Roboto, sans-serif",
          }}
        >
          <Container>
            <Routes>
              <Route path="/" element={<LoginPage onLogin={handleLogin} />} />
              <Route
                path="/reservations"
                element={isLoggedIn ? <ReservationList /> : <LoginPage onLogin={handleLogin} />}
              />
              <Route
                path="/reserve"
                element={isLoggedIn ? <ReservationForm /> : <LoginPage onLogin={handleLogin} />}
              />
            </Routes>
          </Container>
        </Box>
      </div>
    </Router>
  );
}

export default App;
