import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import {
  Grid,
  Card,
  CardContent,
  Typography,
  styled,
  Box,
  Container,
  Chip,
  Divider,
} from '@mui/material';
import axios from '../features/axios';
import dayjs from 'dayjs';  
import 'dayjs/locale/fr';  
dayjs.locale('fr');  

const StyledCard = styled(Card)(({ theme }) => ({
  borderRadius: '16px',  
  boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)', 
  transition: 'transform 0.2s', 
  '&:hover': {
    transform: 'scale(1.02)',
  },
}));

const ChefDashboard = () => {
  const user = useSelector((state) => state.user);
  const [reservations, setReservations] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [departmentName, setDepartmentName] = useState(null); // Store the department name
  const [loadingReservations, setLoadingReservations] = useState(true);
  const [loadingEmployees, setLoadingEmployees] = useState(true);
  const [errorReservations, setErrorReservations] = useState(null);
  const [errorEmployees, setErrorEmployees] = useState(null);
  const [loadingDepartment, setLoadingDepartment] = useState(true);
  const [errorDepartment, setErrorDepartment] = useState(null);

  useEffect(() => {
    // Fetch department name
    const fetchDepartmentName = async () => {
      try {
        if (user && user.numdep) {
          const response = await axios.get('/departments');
          const departments = response.data;

          const foundDepartment = departments.find(dept => dept.id === user.numdep);
          if (foundDepartment) {
            setDepartmentName(foundDepartment.nomdepart);
          } else {
            setErrorDepartment("Département non trouvé.");
          }
        } else {
          setErrorDepartment("Information de l'utilisateur manquante.");
        }
      } catch (error) {
        console.error("Erreur lors de la récupération du département:", error);
        setErrorDepartment("Erreur lors de la récupération du département.");
      } finally {
        setLoadingDepartment(false);
      }
    };

    // Fetch reservations only when departmentName is set
    const fetchReservations = async () => {
      if (!departmentName) return; // Don't fetch until departmentName is available
      try {
        const response = await axios.get('/chef/reservations', {
          params: { department: departmentName },
        });
        setReservations(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération des réservations:", error);
        setErrorReservations("Erreur lors de la récupération des réservations.");
      } finally {
        setLoadingReservations(false);
      }
    };

    // Fetch employees data
    const fetchEmployees = async () => {
      try {
        const response = await axios.get('/chef/employees');
        setEmployees(response.data);
      } catch (error) {
        console.error("Erreur lors de la récupération des employés:", error);
        setErrorEmployees("Erreur lors de la récupération des employés.");
      } finally {
        setLoadingEmployees(false);
      }
    };

    fetchDepartmentName();
    fetchEmployees();  // Fetch employees immediately
  }, [user, departmentName]); // Dependency on user and departmentName

  useEffect(() => {
    // Fetch reservations after departmentName is set
    if (departmentName) {
      fetchReservations();
    }
  }, [departmentName]);  // Fetch reservations when departmentName changes

  const formatDate = (dateString) => {
    return dayjs(dateString).format('D MMM YYYY'); // Format date in French
  };

  const formatTime = (timeString) => {
    return dayjs(timeString, 'HH:mm:ss').format('HH:mm'); // Format time
  };

  if (loadingReservations || loadingEmployees || loadingDepartment) {
    return <Typography variant="h6">Chargement...</Typography>;
  }

  if (errorReservations || errorEmployees || errorDepartment) {
    return (
      <Typography variant="h6" color="error">
        {errorReservations || errorEmployees || errorDepartment}
      </Typography>
    );
  }

  return (
    <Container maxWidth="lg" sx={{ mt: 4 }}>
      <Typography variant="h4" component="h1" gutterBottom sx={{ fontWeight: 'bold', textAlign: 'center' }}>
        Tableau de Bord du Chef
      </Typography>

      <Typography variant="h5" component="h2" gutterBottom sx={{ mt: 3 }}>
        Réservations
      </Typography>
      <Divider sx={{ mb: 2 }} /> {/* Separator */}

      <Grid container spacing={3}>
        {reservations.map((reservation) => (
          <Grid item key={reservation.id} xs={12} sm={6} md={4} lg={3}>
            <StyledCard>
              <CardContent>
                <Typography variant="h6" component="h3" gutterBottom sx={{ fontWeight: 'bold' }}>
                  {reservation.description}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Date: {formatDate(reservation.date)}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Heure: {formatTime(reservation.time)}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Département: {reservation.department}
                </Typography>
                {reservation.status && (
                  <Chip label={reservation.status} color={reservation.status === 'Confirmé' ? 'success' : 'warning'} sx={{ mt: 2 }} />
                )}
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h5" component="h2" gutterBottom sx={{ mt: 3 }}>
        Employés
      </Typography>
      <Divider sx={{ mb: 2 }} />

      <Grid container spacing={3}>
        {employees.map((employee) => (
          <Grid item key={employee.id} xs={12} sm={6} md={4} lg={3}>
            <StyledCard>
              <CardContent>
                <Typography variant="h6" component="h3" gutterBottom sx={{ fontWeight: 'bold' }}>
                  {employee.name}
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  Email: {employee.email}
                </Typography>
              </CardContent>
            </StyledCard>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default ChefDashboard;
