// reducers/userReducer.js
const initialState = {
  user: null,
  token: null,
  role: null,
};

export const userReducer = (state = initialState, action) => {
  switch (action.type) {
    case 'LOGIN':
      return {
        ...state,
        user: action.payload.user,
        token: action.payload.token,
        role: action.payload.role,
      };
    case 'LOGOUT':
      return {
        ...state,
        user: null,
        token: null,
        role: null,
      };
    default:
      return state;
  }
};
