import { createStore, combineReducers } from 'redux';
import { persistStore, persistReducer } from 'redux-persist';
import storage from 'redux-persist/lib/storage'; // default storage is localStorage
import axios from 'axios';

const initialState = {
  users: [],
  reservations: [],
  departments: [],
  employees: [],
  user: null,
  role: null,
};

const userReducer = (state = initialState.user, action) => {
  switch (action.type) {
    case 'LOGIN':
      return action.payload;
    case 'LOGOUT':
      return null;
    default:
      return state;
  }
};

const roleReducer = (state = initialState.role, action) => {
  switch (action.type) {
    case 'LOGIN':
      return action.payload.role;
    case 'LOGOUT':
      return null;
    default:
      return state;
  }
};

const usersReducer = (state = initialState.users, action) => {
  switch (action.type) {
    case 'GET_USERS':
      return action.payload;
    default:
      return state;
  }
};

const reservationsReducer = (state = initialState.reservations, action) => {
  switch (action.type) {
    case 'GET_RESERVATIONS':
      return action.payload;
    default:
      return state;
  }
};

const departmentsReducer = (state = initialState.departments, action) => {
  switch (action.type) {
    case 'GET_DEPARTMENTS':
      return action.payload;
    default:
      return state;
  }
};

const employeesReducer = (state = initialState.employees, action) => {
  switch (action.type) {
    case 'GET_EMPLOYEES':
      return action.payload;
    default:
      return state;
  }
};

// Redux Persist configuration
const persistConfig = {
  key: 'root',
  storage,
  whitelist: ['user', 'role'], // Only persist the user and role slices
};

const rootReducer = combineReducers({
  user: userReducer,
  role: roleReducer,
  users: usersReducer,
  reservations: reservationsReducer,
  departments: departmentsReducer,
  employees: employeesReducer,
});

const persistedReducer = persistReducer(persistConfig, rootReducer);

// Create store with persisted reducer
const store = createStore(persistedReducer);

const persistor = persistStore(store);

export { store, persistor };
