import React, { useState } from 'react';
import { AppBar, Toolbar, Container, Typography, Button, Stack, Drawer, List, ListItem, ListItemButton, ListItemIcon, ListItemText, Box, IconButton } from '@mui/material';
import { Link, useNavigate } from 'react-router-dom'; 
import { useSelector, useDispatch } from 'react-redux';
import { logout } from '../actions';
import MenuIcon from '@mui/icons-material/Menu';
import DashboardIcon from '@mui/icons-material/Dashboard'; 
import { useTheme } from '@mui/material/styles'; 
import useMediaQuery from '@mui/material/useMediaQuery'; 


const Navbar = () => {
  const dispatch = useDispatch();
  const user = useSelector((state) => state.user);
  const navigate = useNavigate(); 
  const [isDrawerOpen, setIsDrawerOpen] = useState(false); 
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down('sm')); 

  const handleLogout = () => {
    dispatch(logout());
    navigate('/');
  };

  const toggleDrawer = (open) => (event) => {
    if (event.type === 'keydown' && (event.key === 'Tab' || event.key === 'Shift')) {
      return;
    }
    setIsDrawerOpen(open);
  };

  const drawerList = (
    <Box sx={{ width: 250 }}>
      <List>
        {user && user.role === 'client' && ( 
          <ListItem disablePadding>
            <ListItemButton component={Link} to="/dashboard">
              <ListItemIcon><DashboardIcon /></ListItemIcon>
              <ListItemText primary="Dashboard" />
            </ListItemButton>
          </ListItem>
        )}
         {user && user.role === 'chef' && (
          <ListItem disablePadding>
            <ListItemButton component={Link} to="/chef">
              <ListItemIcon><DashboardIcon /></ListItemIcon>
              <ListItemText primary="Chef Dashboard" />
            </ListItemButton>
          </ListItem>
        )}
        {user && user.role === 'admin' && (
          <ListItem disablePadding>
            <ListItemButton component={Link} to="/admin">
              <ListItemIcon><DashboardIcon /></ListItemIcon>
              <ListItemText primary="Admin Dashboard" />
            </ListItemButton>
          </ListItem>
        )}
          {user && (
          <ListItem disablePadding>
            <ListItemButton onClick={handleLogout}>
              <ListItemIcon><DashboardIcon /></ListItemIcon>
              <ListItemText primary="Déconnexion" />
            </ListItemButton>
          </ListItem>
        )}
      </List>
    </Box>
  );

  return (
    <AppBar position="static" sx={{ backgroundColor: '#232323', color: '#fff', py: 1, boxShadow: 2 }}>
      <Container maxWidth="lg">
        <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>{/* Added alignItems */}

         {isMobile && ( // Show menu icon only on mobile
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={toggleDrawer(true)}
              sx={{ mr: 2 }}
            >
              <MenuIcon />
            </IconButton>
          )}

          <Typography variant="h5" component="h2" fontFamily="'Roboto', sans-serif" fontWeight="500" sx={{ display: { xs: 'block', sm: 'block' } }}> {/* Conditional display */}
            Tribunal de Commerce à Casablanca
          </Typography>

          {!isMobile && ( // Show buttons only on larger screens
            <Stack direction="row" spacing={2} alignItems="center">
              {!user ? (
                <>
                  <Button component={Link} to="/" sx={linkStyle}>Accueil</Button>
                  <Button component={Link} to="/login" sx={linkStyle}>Connexion</Button>
                  <Button component={Link} to="/register" sx={linkStyle}>Inscription</Button>
                </>
              ) : (
                <Button variant="outlined" sx={logoutButtonStyle} onClick={handleLogout}>Déconnexion</Button>
              )}
            </Stack>
          )}

        </Toolbar>
      </Container>

      {/* Drawer Component */}
      <Drawer
        open={isDrawerOpen}
        onClose={toggleDrawer(false)}
      >
        {drawerList}
      </Drawer>
    </AppBar>
  );
};

const linkStyle = {
  textDecoration: 'none',
  color: '#fff',
  fontSize: '1rem',
  textTransform: 'none',
  '&:hover': {
    textDecoration: 'underline',
  },
};

const logoutButtonStyle = {
  color: '#fff',
  borderColor: '#fff',
  textTransform: 'none',
  '&:hover': {
    borderColor: '#ddd',
    backgroundColor: 'rgba(255, 255, 255, 0.1)',
  },
};

const contactButtonStyle = {
  fontSize: '0.875rem',
  textTransform: 'none',
};

const aboutButtonStyle = {
  fontSize: '0.875rem',
  textTransform: 'none',
};

export default Navbar;
