import React from "react";
import { Link } from "react-router-dom";
import {
  AppBar,
  Toolbar,
  Typography,
  Button,
  IconButton,
  Box,
  List,
  ListItem,
  ListItemText,
} from "@mui/material";
import MenuIcon from "@mui/icons-material/Menu";

const Navbar = ({ isLoggedIn, toggleSidebar, handleLogout, handleLogin }) => {
  return (
    <AppBar
      position="sticky"
      sx={{
        background: "linear-gradient(90deg, #3a7bd5, #00d2ff)",
        boxShadow: "0px 4px 10px rgba(0,0,0,0.3)",
      }}
    >
      <Toolbar>
        {isLoggedIn && (
          <IconButton edge="start" color="inherit" onClick={toggleSidebar} sx={{ marginRight: 2 }}>
            <MenuIcon />
          </IconButton>
        )}
        <Typography variant="h6" sx={{ flexGrow: 1, fontFamily: "Poppins, sans-serif" }}>
          Mon Application de Réservation
        </Typography>

        {isLoggedIn ? (
          <Button
            color="inherit"
            onClick={handleLogout}
            sx={{
              fontFamily: "Roboto, sans-serif",
              "&:hover": {
                backgroundColor: "#ff6f61",
                color: "#fff",
              },
            }}
          >
            Se Déconnecter
          </Button>
        ) : (
          <Button
            color="inherit"
            component={Link}
            to="/"
            sx={{
              fontFamily: "Roboto, sans-serif",
              "&:hover": {
                backgroundColor: "#ff6f61",
                color: "#fff",
              },
            }}
          >
            Se Connecter
          </Button>
        )}
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
