import React, { useState } from 'react';
import { TextField, Button, Box, Typography, Grid, Container, Paper, CircularProgress } from '@mui/material';
import { useDispatch } from 'react-redux';
import { login } from '../features/actions';
import axios from '../features/axios';

const Login = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const dispatch = useDispatch();

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);
    setError(null); 

    try {
      const response = await axios.post('/login', { email, password }); // Use the correct backend route

      dispatch(login({
        user: response.data, 
        role: response.data.role,
        name: response.data.name,  
        numdep: response.data.numdep, 
      }));

      window.location.href = '/'; 
    } catch (error) {
      console.error("Login error:", error); 
      setError(error.response?.data?.error || 'An error occurred during login.');
    } finally {
      setLoading(false);
    }
  };
  return (
    <Container maxWidth="sm">
      <Paper sx={{ padding: 4, borderRadius: 3, boxShadow: 3, backgroundColor: '#fff' }}>
        <Typography variant="h4" align="center" sx={{ mb: 3, fontWeight: 600 }}>
          Login
        </Typography>
        <form onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Email"
                type="email"
                value={email}
                onChange={(event) => setEmail(event.target.value)}
                variant="outlined"
                required
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                fullWidth
                label="Password"
                type="password"
                value={password}
                onChange={(event) => setPassword(event.target.value)}
                variant="outlined"
                required
              />
            </Grid>
            <Grid item xs={12}>
              <Button
                fullWidth
                type="submit"
                variant="contained"
                color="primary"
                sx={{ py: 1.5 }}
                disabled={loading}
              >
                {loading ? <CircularProgress size={24} color="inherit" /> : 'Login'}
              </Button>
            </Grid>
            {error && (
              <Grid item xs={12}>
                <Typography color="error" variant="body2" align="center">
                  {error}
                </Typography>
              </Grid>
            )}
          </Grid>
        </form>
      </Paper>
    </Container>
  );
};

export default Login;
